import React, { useState } from 'react';

interface JobModalProps {
  onSave: (job: { title: string; description: string; location: string; tags: string[] }) => void;
  onClose: () => void;
  existingJob?: {
    title: string;
    description: string;
    location: string;
    tags: string[];
  };
  existingSlugs: string[];
}

const JobModal: React.FC<JobModalProps> = ({ onSave, onClose, existingJob, existingSlugs }) => {
  const [title, setTitle] = useState(existingJob?.title || '');
  const [description, setDescription] = useState(existingJob?.description || '');
  const [location, setLocation] = useState(existingJob?.location || '');
  const [tags, setTags] = useState(existingJob?.tags.join(', ') || '');
  const [error, setError] = useState('');

  const slug = title.trim().toLowerCase().replace(/\s+/g, '-');
  const isSlugUnique = !existingSlugs.includes(slug);

  const handleSave = () => {
    if (!title.trim()) {
      setError('Title is required.');
      return;
    }
    if (!isSlugUnique) {
      setError('Slug must be unique.');
      return;
    }
    onSave({
      title,
      description,
      location,
      tags: tags.split(',').map(t => t.trim()).filter(Boolean),
    });
  };

  return (
    <div className="modal">
      <div className="modal-content shadow-lg animate-fade-in">
        <button className="modal-close" onClick={onClose} title="Close">&times;</button>
        <h2 className="modal-title">{existingJob ? 'Edit Job' : 'Create Job'}</h2>
        {error && <div className="error">{error}</div>}
        <div className="modal-form">
          <label>
            Title <span className="required">*</span>
            <input
              type="text"
              className="form-input"
              placeholder="Job Title"
              value={title}
              onChange={e => setTitle(e.target.value)}
              autoFocus
            />
          </label>
          <label>
            Location
            <input
              type="text"
              className="form-input"
              placeholder="Location"
              value={location}
              onChange={e => setLocation(e.target.value)}
            />
          </label>
          <label>
            Description
            <textarea
              className="form-input"
              placeholder="Job Description"
              value={description}
              onChange={e => setDescription(e.target.value)}
              rows={3}
            />
          </label>
          <label>
            Tags <span className="hint">(comma separated)</span>
            <input
              type="text"
              className="form-input"
              placeholder="e.g. React, TypeScript"
              value={tags}
              onChange={e => setTags(e.target.value)}
            />
          </label>
        </div>
        <div className="modal-actions">
          <button className="btn btn-primary" onClick={handleSave}>
            Save
          </button>
          <button className="btn btn-secondary" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default JobModal;
