import React, { useState } from 'react';

interface JobModalProps {
  onSave: (job: { title: string; description: string; location: string; tags: string[] }) => void;
  onClose: () => void;
  existingJob?: {
    title: string;
    description: string;
    location: string;
    tags: string[];
  };
  existingSlugs: string[];
}

const JobModal: React.FC<JobModalProps> = ({ onSave, onClose, existingJob, existingSlugs }) => {
  const [title, setTitle] = useState(existingJob?.title || '');
  const [description, setDescription] = useState(existingJob?.description || '');
  const [location, setLocation] = useState(existingJob?.location || '');
  const [tags, setTags] = useState(existingJob?.tags.join(', ') || '');
  const [error, setError] = useState('');

  const slug = title.trim().toLowerCase().replace(/\s+/g, '-');
  const isSlugUnique = !existingSlugs.includes(slug);

  const handleSave = () => {
    if (!title.trim()) {
      setError('Title is required.');
      return;
    }
    if (!isSlugUnique) {
      setError('Slug must be unique.');
      return;
    }
    onSave({
      title,
      description,
      location,
      tags: tags.split(',').map(t => t.trim()).filter(Boolean),
    });
    onClose();
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h3>{existingJob ? 'Edit Job' : 'Create Job'}</h3>
        {error && <div className="error">{error}</div>}
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={e => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={e => setLocation(e.target.value)}
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={e => setDescription(e.target.value)}
        />
        <input
          type="text"
          placeholder="Tags (comma separated)"
          value={tags}
          onChange={e => setTags(e.target.value)}
        />
        <div className="modal-actions">
          <button className="btn btn-primary" onClick={handleSave}>
            Save
          </button>
          <button className="btn" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default JobModal;
