import React, { useState, useEffect } from 'react';
import { FixedSizeList as List } from 'react-window';
import { Candidate, Job, ApiResponse } from '../types';
import CandidateModal from './CandidateModal';

const stages = ['applied', 'screen', 'tech', 'offer', 'hired', 'rejected'];

const CandidateList: React.FC = () => {
  const [candidates, setCandidates] = useState<Candidate[]>([]);
  const [jobs, setJobs] = useState<Job[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [stageFilter, setStageFilter] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingCandidate, setEditingCandidate] = useState<Candidate | null>(null);

  useEffect(() => {
    loadCandidates();
    loadJobs();
  }, [searchTerm, stageFilter]);

  const loadCandidates = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        search: searchTerm,
        stage: stageFilter,
        page: '1',
        pageSize: '1000' // Load all for virtualization
      });

      const response = await fetch(`/api/candidates?${params}`);
      if (!response.ok) throw new Error('Failed to load candidates');

      const data: ApiResponse<Candidate[]> = await response.json();
      setCandidates(data.data);
    } catch (error) {
      console.error('Error loading candidates:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadJobs = async () => {
    try {
      const response = await fetch('/api/jobs?page=1&pageSize=100');
      if (!response.ok) throw new Error('Failed to load jobs');

      const data: ApiResponse<Job[]> = await response.json();
      setJobs(data.data);
    } catch (error) {
      console.error('Error loading jobs:', error);
    }
  };

  const handleCreateCandidate = async (candidateData: {
    name: string;
    email: string;
    phone: string;
    resume: string;
    jobId: string;
    stage: string;
  }) => {
    try {
      const response = await fetch('/api/candidates', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(candidateData)
      });

      if (!response.ok) throw new Error('Failed to create candidate');

      setShowModal(false);
      loadCandidates();
    } catch (error) {
      console.error('Error creating candidate:', error);
      alert('Failed to create candidate. Please try again.');
    }
  };

  const handleEditCandidate = async (candidateData: {
    name: string;
    email: string;
    phone: string;
    resume: string;
    jobId: string;
    stage: string;
  }) => {
    if (!editingCandidate) return;

    try {
      const response = await fetch(`/api/candidates/${editingCandidate.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(candidateData)
      });

      if (!response.ok) throw new Error('Failed to update candidate');

      setShowModal(false);
      setEditingCandidate(null);
      loadCandidates();
    } catch (error) {
      console.error('Error updating candidate:', error);
      alert('Failed to update candidate. Please try again.');
    }
  };

  const handleMoveStage = async (candidate: Candidate, newStage: string) => {
    try {
      const response = await fetch(`/api/candidates/${candidate.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          stage: newStage,
          notes: `Moved from ${candidate.stage} to ${newStage}`
        })
      });

      if (!response.ok) throw new Error('Failed to update candidate stage');

      loadCandidates();
    } catch (error) {
      console.error('Error updating candidate stage:', error);
      alert('Failed to update candidate stage. Please try again.');
    }
  };

  const filteredCandidates = candidates.filter(candidate =>
    (candidate.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      candidate.email.toLowerCase().includes(searchTerm.toLowerCase())) &&
    (stageFilter ? candidate.stage === stageFilter : true)
  );

  const getStageBadge = (stage: string) => {
    const badgeClasses = {
      applied: 'badge-info',
      screen: 'badge-warning',
      tech: 'badge-warning',
      offer: 'badge-success',
      hired: 'badge-success',
      rejected: 'badge-danger'
    };
    return <span className={`badge ${badgeClasses[stage as keyof typeof badgeClasses] || 'badge-info'}`}>{stage}</span>;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading candidates...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Candidate Management</h2>
          <button
            className="btn btn-primary"
            onClick={() => { setEditingCandidate(null); setShowModal(true); }}
          >
            Add Candidate
          </button>
        </div>

        <div className="form-group flex gap-2">
          <input
            type="text"
            className="form-input"
            placeholder="Search candidates..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <select
            className="form-input"
            value={stageFilter}
            onChange={e => setStageFilter(e.target.value)}
          >
            <option value="">All Stages</option>
            {stages.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>

        <List
          height={500}
          itemCount={filteredCandidates.length}
          itemSize={90}
          width={'100%'}
        >
          {({ index, style }) => {
            const candidate = filteredCandidates[index];
            return (
              <div key={candidate.id} style={style} className="list-item flex justify-between items-center">
                <div className="flex flex-col gap-2">
                  <div className="flex items-center gap-4">
                    <h3 className="font-semibold">{candidate.name}</h3>
                    {getStageBadge(candidate.stage)}
                  </div>
                  <div className="text-sm text-gray-600">
                    <div>📧 {candidate.email}</div>
                    <div>📞 {candidate.phone}</div>
                    <div>📄 {candidate.resume}</div>
                  </div>
                </div>
                <div className="flex gap-2">
                  <a href={`/candidates/${candidate.id}`} className="btn btn-secondary">View</a>
                  <button
                    className="btn btn-primary"
                    onClick={() => { setEditingCandidate(candidate); setShowModal(true); }}
                  >
                    Edit
                  </button>
                  <select
                    className="form-input"
                    style={{ width: 'auto', padding: '0.375rem 0.5rem', fontSize: '0.75rem' }}
                    value={candidate.stage}
                    onChange={(e) => handleMoveStage(candidate, e.target.value)}
                  >
                    {stages.map(s => (
                      <option key={s} value={s}>
                        Move to {s.charAt(0).toUpperCase() + s.slice(1)}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            );
          }}
        </List>

        {filteredCandidates.length === 0 && (
          <div className="text-center p-6">
            <p>No candidates found matching your search.</p>
          </div>
        )}
      </div>

      {showModal && (
        <CandidateModal
          onSave={editingCandidate ? handleEditCandidate : handleCreateCandidate}
          onClose={() => { setShowModal(false); setEditingCandidate(null); }}
          existingCandidate={editingCandidate || undefined}
          jobs={jobs}
        />
      )}
    </div>
  );
};

export default CandidateList;
