import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import MentionsInput from '../components/MentionsInput';
import { Candidate } from '../types';

const CandidateProfile: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [candidate, setCandidate] = useState<Candidate | null>(null);
  const [allCandidates, setAllCandidates] = useState<Candidate[]>([]);
  const [loading, setLoading] = useState(true);
  const [notes, setNotes] = useState<string[]>([]);
  const [noteInput, setNoteInput] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Fetch the specific candidate
        const candidateResponse = await fetch(`/api/candidates/${id}`);
        if (candidateResponse.ok) {
          const candidateData = await candidateResponse.json();
          setCandidate(candidateData.data);
        }

        // Fetch all candidates for mentions
        const allCandidatesResponse = await fetch('/api/candidates?page=1&pageSize=1000');
        if (allCandidatesResponse.ok) {
          const allCandidatesData = await allCandidatesResponse.json();
          setAllCandidates(allCandidatesData.data);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [id]);

  const handleAddNote = () => {
    if (noteInput.trim()) {
      setNotes([...notes, noteInput]);
      setNoteInput('');
    }
  };

  if (loading) return <div className="container">Loading...</div>;
  if (!candidate) return <div className="container">Candidate not found.</div>;

  return (
    <div className="container">
      <h2>{candidate.name}</h2>
      <div className="mb-2"><strong>Email:</strong> {candidate.email}</div>
      <div className="mb-2"><strong>Phone:</strong> {candidate.phone}</div>
      <div className="mb-2"><strong>Stage:</strong> {candidate.stage}</div>
      <div className="mb-2"><strong>Resume:</strong> {candidate.resume}</div>
      <div className="mb-4"><strong>Status:</strong> {candidate.status}</div>
      <div className="mb-4">
        <strong>Timeline:</strong>
        <ul>
          {candidate.timeline?.map((item, idx) => (
            <li key={idx}>
              {item.stage} - {new Date(item.timestamp).toLocaleString()}
              {item.notes && <> ({item.notes})</>}
            </li>
          ))}
        </ul>
      </div>
      <div className="mb-4">
        <strong>Notes:</strong>
        <ul>
          {notes.map((n, i) => <li key={i}>{n}</li>)}
        </ul>
        <MentionsInput
          value={noteInput}
          onChange={setNoteInput}
          placeholder="Add note with @mention..."
          className="form-input"
          candidates={allCandidates}
        />
        <button className="btn btn-primary ml-2" onClick={handleAddNote}>Add Note</button>
      </div>
    </div>
  );
};

export default CandidateProfile;
