import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import JobModal from './JobModal';
import { DragDropContext, Droppable, Draggable, DropResult } from 'react-beautiful-dnd';
import { Job, ApiResponse } from '../types';

const PAGE_SIZE = 6;

const JobList: React.FC = () => {
  const [jobs, setJobs] = useState<Job[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [tagFilter, setTagFilter] = useState('');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [showModal, setShowModal] = useState(false);
  const [editingJob, setEditingJob] = useState<Job | null>(null);

  useEffect(() => {
    loadJobs();
    // eslint-disable-next-line
  }, [page, filter, statusFilter, tagFilter]);

  const loadJobs = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: page.toString(),
        pageSize: PAGE_SIZE.toString(),
        search: filter,
        status: statusFilter,
        sort: 'order'
      });

      const response = await fetch(`/api/jobs?${params}`);
      if (!response.ok) throw new Error('Failed to load jobs');

      const data: ApiResponse<Job[]> = await response.json();
      setJobs(data.data);
      setTotalPages(data.pagination?.totalPages || 1);
    } catch (error) {
      console.error('Error loading jobs:', error);
    } finally {
      setLoading(false);
    }
  };

  // Filtering logic
  const filteredJobs = jobs.filter(job =>
    job.title.toLowerCase().includes(filter.toLowerCase()) &&
    (statusFilter ? job.status === statusFilter : true) &&
    (tagFilter ? job.tags.includes(tagFilter) : true)
  );

  // Pagination logic
  const paginatedJobs = filteredJobs.slice((page - 1) * PAGE_SIZE, page * PAGE_SIZE);

  const getStatusBadge = (status: string) => {
    const badgeClass = status === 'active' ? 'badge-success' : 'badge-warning';
    return <span className={`badge ${badgeClass}`}>{status}</span>;
  };

  const uniqueTags = Array.from(new Set(jobs.flatMap(job => job.tags)));

  // Modal slug logic
  const existingSlugs = jobs
    .map(job => job.title.trim().toLowerCase().replace(/\s+/g, '-'))
    .filter(slug => !editingJob || slug !== editingJob.title.trim().toLowerCase().replace(/\s+/g, '-'));

  // Create job handler
  const handleCreateJob = async (jobData: { title: string; description: string; location: string; tags: string[] }) => {
    try {
      const response = await fetch('/api/jobs', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...jobData,
          status: 'active',
          order: jobs.length + 1,
        })
      });

      if (!response.ok) throw new Error('Failed to create job');

      setShowModal(false);
      loadJobs();
    } catch (error) {
      console.error('Error creating job:', error);
      alert('Failed to create job. Please try again.');
    }
  };

  // Edit job handler
  const handleEditJob = async (jobData: { title: string; description: string; location: string; tags: string[] }) => {
    if (!editingJob) return;

    try {
      const response = await fetch(`/api/jobs/${editingJob.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(jobData)
      });

      if (!response.ok) throw new Error('Failed to update job');

      setShowModal(false);
      loadJobs();
    } catch (error) {
      console.error('Error updating job:', error);
      alert('Failed to update job. Please try again.');
    }
  };

  // Archive/Unarchive handler
  const handleArchiveJob = async (job: Job) => {
    try {
      const response = await fetch(`/api/jobs/${job.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          status: job.status === 'active' ? 'archived' : 'active'
        })
      });

      if (!response.ok) throw new Error('Failed to update job status');

      loadJobs();
    } catch (error) {
      console.error('Error updating job status:', error);
      alert('Failed to update job status. Please try again.');
    }
  };

  // Drag-and-drop reorder handler
  const handleDragEnd = async (result: DropResult) => {
    if (!result.destination) return;
    const fromIndex = result.source.index;
    const toIndex = result.destination.index;
    if (fromIndex === toIndex) return;

    const movedJob = paginatedJobs[fromIndex];
    const fromOrder = movedJob.order;
    const toOrder = paginatedJobs[toIndex]?.order || (page - 1) * PAGE_SIZE + toIndex + 1;

    // Optimistic reorder
    const reorderedJobs = Array.from(paginatedJobs);
    const [removed] = reorderedJobs.splice(fromIndex, 1);
    reorderedJobs.splice(toIndex, 0, removed);

    // Update order locally
    const updatedJobs = jobs.map(job => {
      const idx = reorderedJobs.findIndex(j => j.id === job.id);
      return idx !== -1 ? { ...job, order: (page - 1) * PAGE_SIZE + idx + 1 } : job;
    });
    setJobs(updatedJobs);

    try {
      const response = await fetch(`/api/jobs/${movedJob.id}/reorder`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ fromOrder, toOrder })
      });

      if (!response.ok) throw new Error('Failed to reorder');

      loadJobs(); // Reload to get updated order from server
    } catch (error) {
      console.error('Error reordering jobs:', error);
      setJobs(jobs); // Rollback
      alert('Failed to reorder. Please try again.');
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading jobs...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Job Listings</h2>
          <button className="btn btn-primary" onClick={() => { setEditingJob(null); setShowModal(true); }}>
            Create Job
          </button>
        </div>

        <div className="form-group flex gap-2">
          <input
            type="text"
            className="form-input"
            placeholder="Search jobs by title..."
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
          />
          <select
            className="form-input"
            value={statusFilter}
            onChange={e => setStatusFilter(e.target.value)}
          >
            <option value="">All Status</option>
            <option value="active">Active</option>
            <option value="archived">Archived</option>
          </select>
          <select
            className="form-input"
            value={tagFilter}
            onChange={e => setTagFilter(e.target.value)}
          >
            <option value="">All Tags</option>
            {uniqueTags.map(tag => (
              <option key={tag} value={tag}>{tag}</option>
            ))}
          </select>
        </div>

        <DragDropContext onDragEnd={handleDragEnd}> {/* @ts-ignore */}
          <Droppable droppableId="jobs-list"> {/* @ts-ignore */}
            {(provided) => (
              <div className="grid grid-2" ref={provided.innerRef} {...provided.droppableProps}>
                {paginatedJobs.map((job, index) => (
                  <Draggable key={job.id} draggableId={job.id} index={index}>
                    {(provided) => (
                      <div
                        className="card"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <div className="flex justify-between items-center mb-2">
                          <Link to={`/jobs/${job.id}`}>
                            <h3 className="text-lg font-semibold">{job.title}</h3>
                          </Link>
                          {getStatusBadge(job.status)}
                        </div>
                        <p className="mb-3 text-gray-600">{job.description}</p>
                        <div className="mb-3">
                          <strong>Location:</strong> {job.location}
                        </div>
                        <div className="mb-3">
                          <strong>Tags:</strong>
                          <div className="flex gap-2 mt-1">
                            {job.tags.map(tag => (
                              <span key={tag} className="badge badge-info">{tag}</span>
                            ))}
                          </div>
                        </div>
                        <div className="flex gap-2">
                          <button
                            className="btn btn-secondary"
                            onClick={() => { setEditingJob(job); setShowModal(true); }}
                          >
                            Edit
                          </button>
                          <button
                            className={`btn ${job.status === 'active' ? 'btn-danger' : 'btn-success'}`}
                            onClick={() => handleArchiveJob(job)}
                          >
                            {job.status === 'active' ? 'Archive' : 'Unarchive'}
                          </button>
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>

        {/* Pagination Controls */}
        <div className="flex justify-center gap-2 mt-4">
          <button
            className="btn"
            disabled={page === 1}
            onClick={() => setPage(page - 1)}
          >
            Prev
          </button>
          <span>Page {page} of {totalPages}</span>
          <button
            className="btn"
            disabled={page === totalPages}
            onClick={() => setPage(page + 1)}
          >
            Next
          </button>
        </div>

        {paginatedJobs.length === 0 && (
          <div className="text-center p-6">
            <p>No jobs found matching your search.</p>
          </div>
        )}
      </div>
      {/* Modal for create/edit */}
      {showModal && (
        <JobModal
          onSave={editingJob ? handleEditJob : handleCreateJob}
          onClose={() => setShowModal(false)}
          existingJob={editingJob ? {
            title: editingJob.title,
            description: editingJob.description,
            location: editingJob.location,
            tags: editingJob.tags,
          } : undefined}
          existingSlugs={existingSlugs}
        />
      )}
    </div>
  );
};

export default JobList;
