import React, { useState, useEffect } from 'react';
import CandidateListProfessional from '../components/CandidateListProfessional';
import CandidateKanbanSimple from '../components/CandidateKanbanSimple';
import { VirtualizedCandidateList } from '../components/VirtualizedCandidateList';
import { useCandidates } from '../store';
import { useNavigate } from 'react-router-dom';
import { ApiResponse, Candidate } from '../types';

const Candidates: React.FC = () => {
  const [viewMode, setViewMode] = useState<'list' | 'kanban' | 'virtualized'>('list');
  const { candidates, setCandidates, loading, setLoading } = useCandidates();
  const navigate = useNavigate();

  useEffect(() => {
    loadCandidates();
  }, []);

  const loadCandidates = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/candidates?page=1&pageSize=1000');
      if (response.ok) {
        const data: ApiResponse<Candidate[]> = await response.json();
        setCandidates(data.data);
      }
    } catch (error) {
      console.error('Error loading candidates:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCandidateClick = (candidate: any) => {
    navigate(`/candidates/${candidate.id}`);
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
              Candidates
            </h1>
            <p className="text-gray-600 dark:text-gray-400">
              Manage your candidate pipeline
            </p>
          </div>
          <div className="flex bg-white dark:bg-gray-800 rounded-lg p-1 shadow-sm border border-gray-200 dark:border-gray-700">
            <button
              className={`px-3 py-2 text-sm font-medium rounded-md transition-colors ${
                viewMode === 'list'
                  ? 'bg-blue-600 text-white shadow-sm'
                  : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white'
              }`}
              onClick={() => setViewMode('list')}
            >
              List
            </button>
            <button
              className={`px-3 py-2 text-sm font-medium rounded-md transition-colors ${
                viewMode === 'virtualized'
                  ? 'bg-blue-600 text-white shadow-sm'
                  : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white'
              }`}
              onClick={() => setViewMode('virtualized')}
            >
              Virtual
            </button>
            <button
              className={`px-3 py-2 text-sm font-medium rounded-md transition-colors ${
                viewMode === 'kanban'
                  ? 'bg-blue-600 text-white shadow-sm'
                  : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white'
              }`}
              onClick={() => setViewMode('kanban')}
            >
              Kanban
            </button>
          </div>
        </div>

        {viewMode === 'list' && <CandidateListProfessional />}
        {viewMode === 'virtualized' && (
          <VirtualizedCandidateList 
            candidates={candidates}
            loading={loading}
            onCandidateClick={handleCandidateClick}
          />
        )}
        {viewMode === 'kanban' && <CandidateKanbanSimple />}
      </div>
    </div>
  );
};

export default Candidates;
