import React, { useState, useEffect } from 'react';
import AssessmentForm from '../components/AssessmentForm';
import { Candidate, Job, Assessment, AssessmentSubmission } from '../types';

const MyApplications: React.FC = () => {
  const [applications, setApplications] = useState<Candidate[]>([]);
  const [jobs, setJobs] = useState<Job[]>([]);
  const [assessments, setAssessments] = useState<Assessment[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedAssessment, setSelectedAssessment] = useState<Assessment | null>(null);

  // Mock candidate ID - in real app this would come from authentication
  const candidateId = 'candidate-1';

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);

      // Load candidate's applications
      const candidatesResponse = await fetch(`/api/candidates?email=candidate1@example.com`);
      if (candidatesResponse.ok) {
        const candidatesData = await candidatesResponse.json();
        setApplications(candidatesData.data);
      }

      // Load jobs
      const jobsResponse = await fetch('/api/jobs?page=1&pageSize=100');
      if (jobsResponse.ok) {
        const jobsData = await jobsResponse.json();
        setJobs(jobsData.data);
      }

      // Load assessments for candidate's jobs
      const assessmentsPromises = jobs.map(async (job) => {
        try {
          const response = await fetch(`/api/assessments/${job.id}`);
          if (response.ok) {
            const data = await response.json();
            return data.data;
          }
        } catch {
          return null;
        }
        return null;
      });

      const assessmentsResults = await Promise.all(assessmentsPromises);
      const validAssessments = assessmentsResults.filter(a => a !== null) as Assessment[];
      setAssessments(validAssessments);

    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTakeAssessment = (assessment: Assessment) => {
    setSelectedAssessment(assessment);
  };

  const handleSubmitAssessment = async (submission: AssessmentSubmission) => {
    try {
      await fetch('/api/assessments/submit', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(submission)
      });

      setSelectedAssessment(null);
      alert('Assessment submitted successfully!');
    } catch (error) {
      console.error('Error submitting assessment:', error);
      alert('Failed to submit assessment. Please try again.');
    }
  };

  const getStageBadge = (stage: string) => {
    const badgeClasses = {
      applied: 'badge-info',
      screen: 'badge-warning',
      tech: 'badge-warning',
      offer: 'badge-success',
      hired: 'badge-success',
      rejected: 'badge-danger'
    };
    return <span className={`badge ${badgeClasses[stage as keyof typeof badgeClasses] || 'badge-info'}`}>{stage}</span>;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading your applications...</p>
        </div>
      </div>
    );
  }

  if (selectedAssessment) {
    return (
      <AssessmentForm
        assessment={selectedAssessment}
        candidateId={candidateId}
        onSubmit={handleSubmitAssessment}
        onClose={() => setSelectedAssessment(null)}
      />
    );
  }

  return (
    <div className="container">
      <div className="mb-6">
        <h2 className="text-2xl font-bold">My Applications</h2>
        <p className="text-gray-600 mt-2">Track your job applications and complete assessments</p>
      </div>

      <div className="grid grid-2 gap-6">
        {applications.map(application => {
          const job = jobs.find(j => j.id === application.jobId);
          const assessment = assessments.find(a => a.jobId === application.jobId);

          return (
            <div key={application.id} className="card">
              <div className="card-header">
                <h3 className="card-title">{job?.title || 'Unknown Job'}</h3>
                {getStageBadge(application.stage)}
              </div>

              <div className="mb-4">
                <p className="text-sm text-gray-600 mb-2">
                  Applied on {new Date(application.appliedAt).toLocaleDateString()}
                </p>
                <p className="text-sm text-gray-600">
                  üìç {job?.location || 'Location not specified'}
                </p>
              </div>

              {assessment && (
                <div className="mb-4 p-3 bg-blue-50 rounded border border-blue-200">
                  <h4 className="font-medium text-blue-900 mb-2">Assessment Available</h4>
                  <p className="text-sm text-blue-700 mb-3">{assessment.title}</p>
                  <button
                    className="btn btn-primary btn-sm"
                    onClick={() => handleTakeAssessment(assessment)}
                  >
                    Take Assessment
                  </button>
                </div>
              )}

              <div className="border-t pt-4">
                <h4 className="font-medium mb-2">Application Timeline</h4>
                <div className="space-y-2">
                  {application.timeline?.slice().reverse().map((entry, index) => (
                    <div key={index} className="flex justify-between items-center text-sm">
                      <span className="text-gray-600">
                        {new Date(entry.timestamp).toLocaleDateString()}
                      </span>
                      <span className="font-medium">{entry.stage}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {applications.length === 0 && (
        <div className="card text-center p-6">
          <h3 className="text-xl font-semibold mb-2">No Applications Yet</h3>
          <p className="text-gray-600 mb-4">
            You haven't applied to any jobs yet. Browse available jobs to get started.
          </p>
          <a href="/jobs" className="btn btn-primary">Browse Jobs</a>
        </div>
      )}
    </div>
  );
};

export default MyApplications;
