import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import ThemeToggle from './ThemeToggle';

const Header: React.FC<{ onLogout?: () => void }> = ({ onLogout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === 'Escape' && isMenuOpen) {
      setIsMenuOpen(false);
    }
  };

  const isActive = (path: string) => location.pathname === path;

  return (
    <header className="header" onKeyDown={handleKeyDown}>
      <div className="header-content">
        <Link to="/" className="logo" onClick={() => setIsMenuOpen(false)}>
          TalentFlow
        </Link>

        {/* Desktop Navigation */}
        <nav className="nav hidden md:flex">
          <Link
            to="/jobs"
            className={`nav-link ${isActive('/jobs') ? 'active' : ''}`}
          >
            Jobs
          </Link>
          <Link
            to="/candidates"
            className={`nav-link ${isActive('/candidates') ? 'active' : ''}`}
          >
            Candidates
          </Link>
          <Link
            to="/assessments"
            className={`nav-link ${isActive('/assessments') ? 'active' : ''}`}
          >
            Assessments
          </Link>
        </nav>

        {/* Desktop Actions */}
        <div className="hidden md:flex items-center gap-2">
          <ThemeToggle />
          {onLogout && (
            <button className="btn btn-secondary" onClick={onLogout}>
              Logout
            </button>
          )}
        </div>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden btn btn-secondary"
          onClick={toggleMenu}
          aria-label={isMenuOpen ? "Close menu" : "Open menu"}
          aria-expanded={isMenuOpen}
          aria-controls="mobile-navigation"
        >
          {isMenuOpen ? '✕' : '☰'}
        </button>
      </div>

      {/* Mobile Navigation */}
      {isMenuOpen && (
        <nav id="mobile-navigation" className="md:hidden bg-white border-t border-gray-200" role="navigation">
          <div className="container py-4">
            <Link
              to="/jobs"
              className={`block py-2 px-4 text-gray-700 hover:bg-gray-100 ${isActive('/jobs') ? 'bg-blue-50 text-blue-600' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Jobs
            </Link>
            <Link
              to="/candidates"
              className={`block py-2 px-4 text-gray-700 hover:bg-gray-100 ${isActive('/candidates') ? 'bg-blue-50 text-blue-600' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Candidates
            </Link>
            <Link
              to="/assessments"
              className={`block py-2 px-4 text-gray-700 hover:bg-gray-100 ${isActive('/assessments') ? 'bg-blue-50 text-blue-600' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Assessments
            </Link>
            {onLogout && (
              <button
                className="block w-full text-left py-2 px-4 text-gray-700 hover:bg-gray-100"
                onClick={() => {
                  onLogout();
                  setIsMenuOpen(false);
                }}
              >
                Logout
              </button>
            )}
          </div>
        </nav>
      )}
    </header>
  );
};

export default Header;
