import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  UserIcon, 
  EnvelopeIcon, 
  LockClosedIcon, 
  EyeIcon, 
  EyeSlashIcon,
  ShieldCheckIcon,
  BuildingOfficeIcon,
  UserGroupIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../store';
import Button from '../components/ui/Button';
import Input from '../components/ui/Input';
import Card from '../components/ui/Card';
import { registerUser, loginUser } from '../services/userDatabase';
import toast from 'react-hot-toast';

const LoginPage: React.FC = () => {
  const [role, setRole] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const { setUser } = useAuth();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Validation
    if (!password.trim()) {
      toast.error('Please enter your password.');
      return;
    }

    if (password.length < 6) {
      toast.error('Password must be at least 6 characters long.');
      return;
    }

    if (isLogin) {
      // Login validation
      if (!email.trim()) {
        toast.error('Please enter your email.');
        return;
      }
    } else {
      // Registration validation
      if (!role) {
        toast.error('Please select a role.');
        return;
      }

      if (!name.trim()) {
        toast.error('Please enter your name.');
        return;
      }

      if (!email.trim()) {
        toast.error('Please enter your email.');
        return;
      }
    }

    setLoading(true);
    const loadingToast = toast.loading(isLogin ? 'Signing in...' : 'Creating account...');

    try {
      if (isLogin) {
        // Login
        const result = await loginUser(email, password);
        setUser({
          role: result.user.role,
          name: result.user.name,
          email: result.user.email,
        });
        toast.success(`Welcome back, ${result.user.name}!`, { id: loadingToast });
      } else {
        // Register
        await registerUser({
          email,
          name,
          password,
          role: role as 'HR' | 'Candidate'
        });
        toast.success('Account created successfully! Please sign in.', { id: loadingToast });
        setIsLogin(true);
        // Clear form
        setRole('');
        setName('');
        setEmail('');
        setPassword('');
      }
    } catch (error: any) {
      toast.error(error.message || 'Authentication failed', { id: loadingToast });
    } finally {
      setLoading(false);
    }
  };

  const toggleMode = () => {
    setIsLogin(!isLogin);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100 dark:from-slate-900 dark:via-slate-800 dark:to-slate-900 flex">
      {/* Left Side - Branding */}
      <div className="hidden lg:flex lg:flex-1 lg:flex-col lg:justify-center lg:px-12 xl:px-16">
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          className="max-w-md"
        >
          <div className="flex items-center mb-8">
            <div className="h-12 w-12 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-xl flex items-center justify-center shadow-lg mr-4">
              <span className="text-white font-bold text-xl">TF</span>
            </div>
            <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
              TalentFlow
            </h1>
          </div>

          <h2 className="text-4xl font-bold text-gray-900 dark:text-white mb-6 leading-tight">
            Modern Hiring
            <br />
            <span className="text-blue-600 dark:text-blue-400">Made Simple</span>
          </h2>

          <p className="text-lg text-gray-600 dark:text-gray-300 mb-8 leading-relaxed">
            Streamline your recruitment process with our comprehensive platform. 
            Manage jobs, track candidates, and conduct assessments all in one place.
          </p>

          <div className="space-y-4">
            <div className="flex items-center text-gray-600 dark:text-gray-300">
              <ShieldCheckIcon className="w-5 h-5 text-green-500 mr-3" />
              <span>Enterprise-grade security</span>
            </div>
            <div className="flex items-center text-gray-600 dark:text-gray-300">
              <UserGroupIcon className="w-5 h-5 text-blue-500 mr-3" />
              <span>Collaborative hiring workflows</span>
            </div>
            <div className="flex items-center text-gray-600 dark:text-gray-300">
              <BuildingOfficeIcon className="w-5 h-5 text-purple-500 mr-3" />
              <span>Trusted by leading companies</span>
            </div>
          </div>
        </motion.div>
      </div>

      {/* Right Side - Login Form */}
      <div className="flex-1 flex items-center justify-center px-4 sm:px-6 lg:px-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="w-full max-w-md"
        >
          {/* Mobile Logo */}
          <div className="lg:hidden text-center mb-8">
            <div className="mx-auto h-16 w-16 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-2xl flex items-center justify-center shadow-lg mb-4">
              <span className="text-white font-bold text-2xl">TF</span>
            </div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
              TalentFlow
            </h1>
            <p className="text-gray-600 dark:text-gray-400">
              {isLogin ? 'Welcome back! Sign in to continue' : 'Join us and start your journey'}
            </p>
          </div>

          <div className="bg-white dark:bg-slate-800 shadow-2xl rounded-2xl p-8 border border-gray-200 dark:border-slate-700">
            {/* Header */}
            <div className="text-center mb-8">
              <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
                {isLogin ? 'Welcome Back' : 'Create Account'}
              </h2>
              <p className="text-gray-600 dark:text-gray-400">
                {isLogin 
                  ? 'Sign in to access your dashboard' 
                  : 'Join thousands of companies using TalentFlow'
                }
              </p>
            </div>

            {/* Tab Switcher */}
            <div className="flex mb-8 bg-gray-50 dark:bg-slate-700 rounded-xl p-1">
              <button
                onClick={() => setIsLogin(true)}
                className={`flex-1 py-3 px-4 rounded-lg text-sm font-semibold transition-all duration-200 ${
                  isLogin
                    ? 'bg-white dark:bg-slate-600 text-gray-900 dark:text-white shadow-sm'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white'
                }`}
              >
                Sign In
              </button>
              <button
                onClick={() => setIsLogin(false)}
                className={`flex-1 py-3 px-4 rounded-lg text-sm font-semibold transition-all duration-200 ${
                  !isLogin
                    ? 'bg-white dark:bg-slate-600 text-gray-900 dark:text-white shadow-sm'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white'
                }`}
              >
                Register
              </button>
            </div>

          <form className="space-y-6" onSubmit={handleSubmit}>
            {/* Role Selection - Only for Registration */}
            {!isLogin && (
              <div className="mb-6">
                <label className="block text-sm font-semibold text-gray-700 dark:text-gray-300 mb-4">
                  I am joining as a...
                </label>
                <div className="grid grid-cols-2 gap-4">
                  <motion.button
                    type="button"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    onClick={() => setRole('HR')}
                    className={`flex flex-col items-center justify-center px-4 py-6 border-2 rounded-xl text-sm font-medium transition-all duration-200 ${
                      role === 'HR'
                        ? 'border-blue-500 bg-blue-50 dark:bg-blue-900/20 text-blue-700 dark:text-blue-300'
                        : 'border-gray-200 dark:border-slate-600 bg-white dark:bg-slate-700 text-gray-700 dark:text-gray-300 hover:border-gray-300 dark:hover:border-slate-500'
                    }`}
                  >
                    <BuildingOfficeIcon className="w-8 h-8 mb-2" />
                    <span className="font-semibold">HR Manager</span>
                    <span className="text-xs text-gray-500 dark:text-gray-400 mt-1">Hire & manage talent</span>
                  </motion.button>
                  <motion.button
                    type="button"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    onClick={() => setRole('Candidate')}
                    className={`flex flex-col items-center justify-center px-4 py-6 border-2 rounded-xl text-sm font-medium transition-all duration-200 ${
                      role === 'Candidate'
                        ? 'border-blue-500 bg-blue-50 dark:bg-blue-900/20 text-blue-700 dark:text-blue-300'
                        : 'border-gray-200 dark:border-slate-600 bg-white dark:bg-slate-700 text-gray-700 dark:text-gray-300 hover:border-gray-300 dark:hover:border-slate-500'
                    }`}
                  >
                    <UserIcon className="w-8 h-8 mb-2" />
                    <span className="font-semibold">Candidate</span>
                    <span className="text-xs text-gray-500 dark:text-gray-400 mt-1">Find opportunities</span>
                  </motion.button>
                </div>
              </div>
            )}

            {/* Name Field - Only for Registration */}
            {!isLogin && (
              <Input
                label="Full Name"
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Enter your full name"
                icon={<UserIcon />}
                required
                fullWidth
              />
            )}

            {/* Email Field */}
            <Input
              label="Email Address"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              icon={<EnvelopeIcon />}
              required
              fullWidth
            />

            {/* Password Field */}
            <div className="relative">
              <Input
                label="Password"
                type={showPassword ? 'text' : 'password'}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                helperText="Minimum 6 characters"
                icon={<LockClosedIcon />}
                required
                fullWidth
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="absolute right-3 top-9 text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition-colors"
              >
                {showPassword ? (
                  <EyeSlashIcon className="w-5 h-5" />
                ) : (
                  <EyeIcon className="w-5 h-5" />
                )}
              </button>
            </div>

            {/* Submit Button */}
            <Button
              type="submit"
              variant="primary"
              size="lg"
              loading={loading}
              className="w-full bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white font-semibold py-3 shadow-lg"
            >
              {isLogin ? 'Sign In to Dashboard' : 'Create My Account'}
            </Button>

            {/* Additional Options */}
            {isLogin && (
              <div className="mt-6 text-center">
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  Demo Credentials:
                </p>
                <div className="mt-2 space-y-1 text-xs text-gray-500 dark:text-gray-400">
                  <p>HR: hr@demo.com / password123</p>
                  <p>Candidate: candidate@demo.com / password123</p>
                </div>
              </div>
            )}
          </form>

          {/* Footer */}
          <div className="mt-8 pt-6 border-t border-gray-200 dark:border-slate-600">
            <p className="text-center text-xs text-gray-500 dark:text-gray-400">
              By continuing, you agree to our{' '}
              <a href="#" className="text-blue-600 hover:text-blue-700 dark:text-blue-400">
                Terms of Service
              </a>{' '}
              and{' '}
              <a href="#" className="text-blue-600 hover:text-blue-700 dark:text-blue-400">
                Privacy Policy
              </a>
            </p>
          </div>
        </div>
        </motion.div>
      </div>
    </div>
  );
};

export default LoginPage;
