import React, { useState, useEffect } from 'react';
import { Candidate, Job } from '../types';

interface CandidateModalProps {
  onSave: (candidate: {
    name: string;
    email: string;
    phone: string;
    resume: string;
    jobId: string;
    stage: string;
  }) => void;
  onClose: () => void;
  existingCandidate?: Candidate;
  jobs: Job[];
}

const stages = ['applied', 'screen', 'tech', 'offer', 'hired', 'rejected'];

const CandidateModal: React.FC<CandidateModalProps> = ({
  onSave,
  onClose,
  existingCandidate,
  jobs
}) => {
  const [name, setName] = useState(existingCandidate?.name || '');
  const [email, setEmail] = useState(existingCandidate?.email || '');
  const [phone, setPhone] = useState(existingCandidate?.phone || '');
  const [resume, setResume] = useState(existingCandidate?.resume || '');
  const [jobId, setJobId] = useState(existingCandidate?.jobId || '');
  const [stage, setStage] = useState(existingCandidate?.stage || 'applied');
  const [error, setError] = useState('');

  const handleSave = () => {
    if (!name.trim()) {
      setError('Name is required.');
      return;
    }
    if (!email.trim()) {
      setError('Email is required.');
      return;
    }
    if (!jobId) {
      setError('Job selection is required.');
      return;
    }

    onSave({
      name,
      email,
      phone,
      resume,
      jobId,
      stage,
    });
  };

  return (
    <div className="modal">
      <div className="modal-content shadow-lg animate-fade-in">
        <button className="modal-close" onClick={onClose} title="Close">&times;</button>
        <h2 className="modal-title">{existingCandidate ? 'Edit Candidate' : 'Add Candidate'}</h2>
        {error && <div className="error">{error}</div>}
        <div className="modal-form">
          <label>
            Name <span className="required">*</span>
            <input
              type="text"
              className="form-input"
              placeholder="Full Name"
              value={name}
              onChange={e => setName(e.target.value)}
              autoFocus
            />
          </label>
          <label>
            Email <span className="required">*</span>
            <input
              type="email"
              className="form-input"
              placeholder="email@example.com"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </label>
          <label>
            Phone
            <input
              type="tel"
              className="form-input"
              placeholder="+1-555-0123"
              value={phone}
              onChange={e => setPhone(e.target.value)}
            />
          </label>
          <label>
            Resume
            <textarea
              className="form-input"
              placeholder="Resume summary or link"
              value={resume}
              onChange={e => setResume(e.target.value)}
              rows={3}
            />
          </label>
          <label>
            Job <span className="required">*</span>
            <select
              className="form-input"
              value={jobId}
              onChange={e => setJobId(e.target.value)}
            >
              <option value="">Select a job</option>
              {jobs.map(job => (
                <option key={job.id} value={job.id}>
                  {job.title}
                </option>
              ))}
            </select>
          </label>
          <label>
            Stage
            <select
              className="form-input"
              value={stage}
              onChange={e => setStage(e.target.value)}
            >
              {stages.map(s => (
                <option key={s} value={s}>
                  {s.charAt(0).toUpperCase() + s.slice(1)}
                </option>
              ))}
            </select>
          </label>
        </div>
        <div className="modal-actions">
          <button className="btn btn-primary" onClick={handleSave}>
            Save
          </button>
          <button className="btn btn-secondary" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default CandidateModal;
