import { useParams, useNavigate } from 'react-router-dom';
import MentionsInput from '../components/MentionsInput';
import { Candidate, Job, TimelineEntry } from '../types';

const CandidateProfile: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [candidate, setCandidate] = useState<Candidate | null>(null);
  const [job, setJob] = useState<Job | null>(null);
  const [allCandidates, setAllCandidates] = useState<Candidate[]>([]);
  const [loading, setLoading] = useState(true);
  const [notes, setNotes] = useState<string[]>([]);
  const [noteInput, setNoteInput] = useState('');
  const [showMoveModal, setShowMoveModal] = useState(false);
  const [newStage, setNewStage] = useState('');

  const stages = ['applied', 'screen', 'tech', 'offer', 'hired', 'rejected'];

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Fetch the specific candidate
        const candidateResponse = await fetch(`/api/candidates/${id}`);
        if (candidateResponse.ok) {
          const candidateData = await candidateResponse.json();
          setCandidate(candidateData.data);

          // Fetch the job details
          if (candidateData.data.jobId) {
            const jobResponse = await fetch(`/api/jobs/${candidateData.data.jobId}`);
            if (jobResponse.ok) {
              const jobData = await jobResponse.json();
              setJob(jobData.data);
            }
          }
        }

        // Fetch all candidates for mentions
        const allCandidatesResponse = await fetch('/api/candidates?page=1&pageSize=1000');
        if (allCandidatesResponse.ok) {
          const allCandidatesData = await allCandidatesResponse.json();
          setAllCandidates(allCandidatesData.data);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [id]);

  const handleAddNote = () => {
    if (noteInput.trim()) {
      setNotes([...notes, noteInput]);
      setNoteInput('');
    }
  };

  const handleMoveStage = async () => {
    if (!candidate || !newStage) return;

    try {
      const response = await fetch(`/api/candidates/${candidate.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          stage: newStage,
          notes: `Moved from ${candidate.stage} to ${newStage}`
        })
      });

      if (!response.ok) throw new Error('Failed to update candidate stage');

      // Update local state
      const updatedCandidate = { ...candidate, stage: newStage };
      setCandidate(updatedCandidate);
      setShowMoveModal(false);
      setNewStage('');
    } catch (error) {
      console.error('Error updating candidate stage:', error);
      alert('Failed to update candidate stage. Please try again.');
    }
  };

  const getStageBadge = (stage: string) => {
    const badgeClasses = {
      applied: 'badge-info',
      screen: 'badge-warning',
      tech: 'badge-warning',
      offer: 'badge-success',
      hired: 'badge-success',
      rejected: 'badge-danger'
    };
    return <span className={`badge ${badgeClasses[stage as keyof typeof badgeClasses] || 'badge-info'}`}>{stage}</span>;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading candidate profile...</p>
        </div>
      </div>
    );
  }

  if (!candidate) {
    return (
      <div className="container">
        <div className="text-center">
          <h2>Candidate Not Found</h2>
          <p>The candidate you're looking for doesn't exist.</p>
          <button className="btn btn-primary" onClick={() => navigate('/candidates')}>
            Back to Candidates
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="flex justify-between items-center mb-6">
        <div className="flex items-center gap-4">
          <button
            className="btn btn-secondary"
            onClick={() => navigate('/candidates')}
          >
            ‚Üê Back to Candidates
          </button>
          <div>
            <h1 className="text-3xl font-bold">{candidate.name}</h1>
            {getStageBadge(candidate.stage)}
          </div>
        </div>
        <button
          className="btn btn-primary"
          onClick={() => { setNewStage(candidate.stage); setShowMoveModal(true); }}
        >
          Move Stage
        </button>
      </div>

      <div className="grid grid-2 gap-6">
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Candidate Information</h2>
          </div>
          <div className="card-content">
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
              <div className="flex items-center gap-2">
                <span>üìß</span>
                <a href={`mailto:${candidate.email}`} className="text-blue-600 hover:underline">
                  {candidate.email}
                </a>
              </div>
            </div>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Phone</label>
              <div className="flex items-center gap-2">
                <span>üìû</span>
                <a href={`tel:${candidate.phone}`} className="text-blue-600 hover:underline">
                  {candidate.phone}
                </a>
              </div>
            </div>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Applied Date</label>
              <div className="flex items-center gap-2">
                <span>üìÖ</span>
                <span>{new Date(candidate.appliedAt).toLocaleDateString()}</span>
              </div>
            </div>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Status</label>
              <span className={`badge ${candidate.status === 'active' ? 'badge-success' : 'badge-secondary'}`}>
                {candidate.status}
              </span>
            </div>
            {job && (
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-1">Applied Position</label>
                <div className="flex items-center gap-2">
                  <span>üíº</span>
                  <a href={`/jobs/${job.id}`} className="text-blue-600 hover:underline">
                    {job.title}
                  </a>
                </div>
              </div>
            )}
          </div>
        </div>

        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Resume</h2>
          </div>
          <div className="card-content">
            <div className="bg-gray-50 p-4 rounded-lg">
              <pre className="whitespace-pre-wrap text-sm">{candidate.resume}</pre>
            </div>
          </div>
        </div>
      </div>

      <div className="card mt-6">
        <div className="card-header">
          <h2 className="card-title">Timeline</h2>
        </div>
        <div className="card-content">
          <div className="timeline">
            {candidate.timeline?.map((item: TimelineEntry, idx: number) => (
              <div key={item.id} className="timeline-item">
                <div className="timeline-marker"></div>
                <div className="timeline-content">
                  <div className="flex justify-between items-start mb-2">
                    <h4 className="font-semibold">{item.stage}</h4>
                    <span className="text-sm text-gray-500">
                      {new Date(item.timestamp).toLocaleString()}
                    </span>
                  </div>
                  {item.notes && (
                    <p className="text-gray-700">{item.notes}</p>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="card mt-6">
        <div className="card-header">
          <h2 className="card-title">Notes</h2>
        </div>
        <div className="card-content">
          {notes.length > 0 && (
            <div className="mb-4">
              <ul className="space-y-2">
                {notes.map((note, i) => (
                  <li key={i} className="bg-gray-50 p-3 rounded-lg">
                    {note}
                  </li>
                ))}
              </ul>
            </div>
          )}
          <div className="flex gap-2">
            <MentionsInput
              value={noteInput}
              onChange={setNoteInput}
              placeholder="Add note with @mention..."
              className="form-input flex-1"
              candidates={allCandidates}
            />
            <button
              className="btn btn-primary"
              onClick={handleAddNote}
              disabled={!noteInput.trim()}
            >
              Add Note
            </button>
          </div>
        </div>
      </div>

      {showMoveModal && (
        <div className="modal">
          <div className="modal-content">
            <button className="modal-close" onClick={() => setShowMoveModal(false)}>&times;</button>
            <h2 className="modal-title">Move Candidate to New Stage</h2>
            <div className="modal-form">
              <label>
                Current Stage: {getStageBadge(candidate.stage)}
              </label>
              <label>
                New Stage
                <select
                  className="form-input"
                  value={newStage}
                  onChange={e => setNewStage(e.target.value)}
                >
                  <option value="">Select new stage</option>
                  {stages.map(s => (
                    <option key={s} value={s} disabled={s === candidate.stage}>
                      {s.charAt(0).toUpperCase() + s.slice(1)}
                    </option>
                  ))}
                </select>
              </label>
            </div>
            <div className="modal-actions">
              <button className="btn btn-primary" onClick={handleMoveStage}>
                Move Stage
              </button>
              <button className="btn btn-secondary" onClick={() => setShowMoveModal(false)}>
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CandidateProfile;
