import React, { useState } from 'react';

const JobList = () => {
    const [jobs, setJobs] = useState([]);
    const [filter, setFilter] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const jobsPerPage = 10;

    const filteredJobs = jobs.filter(job => job.title.toLowerCase().includes(filter.toLowerCase()));
    const totalPages = Math.ceil(filteredJobs.length / jobsPerPage);
    const currentJobs = filteredJobs.slice((currentPage - 1) * jobsPerPage, currentPage * jobsPerPage);

    const handleCreateJob = () => {
        // Logic to create a new job
    };

    const handleEditJob = (jobId) => {
        // Logic to edit a job
    };

    const handleArchiveJob = (jobId) => {
        // Logic to archive a job
    };

    const handleReorderJobs = (jobId, direction) => {
        // Logic to reorder jobs
    };

    return (
        <div>
            <h2>Job List</h2>
            <input 
                type="text" 
                placeholder="Filter jobs..." 
                value={filter} 
                onChange={(e) => setFilter(e.target.value)} 
            />
            <button onClick={handleCreateJob}>Create Job</button>
            <ul>
                {currentJobs.map(job => (
                    <li key={job.id}>
                        {job.title}
                        <button onClick={() => handleEditJob(job.id)}>Edit</button>
                        <button onClick={() => handleArchiveJob(job.id)}>Archive</button>
                        <button onClick={() => handleReorderJobs(job.id, 'up')}>Move Up</button>
                        <button onClick={() => handleReorderJobs(job.id, 'down')}>Move Down</button>
                    </li>
                ))}
            </ul>
            <div>
                <button 
                    onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))} 
                    disabled={currentPage === 1}
                >
                    Previous
                </button>
                <span>Page {currentPage} of {totalPages}</span>
                <button 
                    onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))} 
                    disabled={currentPage === totalPages}
                >
                    Next
                </button>
            </div>
        </div>
    );
};

export default JobList;
