// Simple API simulation
export const delay = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));

export const jobsApi = {
  getJobs: async () => {
    await delay(500);
    return { data: mockJobs, pagination: { page: 1, pageSize: 10, total: mockJobs.length, totalPages: 1 } };
  },
  createJob: async (job: any) => {
    await delay(300);
    const newJob = { ...job, id: Date.now().toString(), createdAt: new Date().toISOString(), updatedAt: new Date().toISOString() };
    mockJobs.push(newJob);
    return newJob;
  },
  updateJob: async (id: string, updates: any) => {
    await delay(300);
    const index = mockJobs.findIndex(job => job.id === id);
    if (index >= 0) {
      mockJobs[index] = { ...mockJobs[index], ...updates, updatedAt: new Date().toISOString() };
      return mockJobs[index];
    }
    throw new Error('Job not found');
  }
};

