import Dexie, { Table } from 'dexie';
import { Job, Candidate, Assessment, AssessmentSubmission, TimelineEntry } from '../types';

export class TalentFlowDatabase extends Dexie {
  jobs!: Table<Job>;
  candidates!: Table<Candidate>;
  assessments!: Table<Assessment>;
  submissions!: Table<AssessmentSubmission>;
  timeline!: Table<TimelineEntry>;

  constructor() {
    super('TalentFlowDatabase');
    this.version(1).stores({
      jobs: 'id, title, status, order, createdAt',
      candidates: 'id, name, email, stage, jobId, appliedAt',
      assessments: 'id, jobId, title, createdAt',
      submissions: 'id, jobId, candidateId, submittedAt',
      timeline: 'id, candidateId, stage, timestamp'
    });
  }
}

export const db = new TalentFlowDatabase();

// Initialize database with seed data
export const initializeDatabase = async () => {
  const jobCount = await db.jobs.count();
  if (jobCount === 0) {
    await seedDatabase();
  }
};

const seedDatabase = async () => {
  // Seed Jobs
  const jobs: Job[] = Array.from({ length: 25 }, (_, i) => ({
    id: `job-${i + 1}`,
    title: [
      'Senior Frontend Developer',
      'Backend Engineer',
      'Full Stack Developer',
      'DevOps Engineer',
      'Product Manager',
      'UX Designer',
      'Data Scientist',
      'Mobile Developer',
      'QA Engineer',
      'Technical Lead',
      'Software Architect',
      'Machine Learning Engineer',
      'Cloud Engineer',
      'Security Engineer',
      'Database Administrator',
      'Site Reliability Engineer',
      'Business Analyst',
      'Scrum Master',
      'UI Designer',
      'Marketing Manager',
      'Sales Representative',
      'Customer Success Manager',
      'HR Specialist',
      'Finance Analyst',
      'Operations Manager'
    ][i],
    description: `We are looking for a talented professional to join our team. This role involves working with cutting-edge technologies and collaborating with cross-functional teams.`,
    location: ['Remote', 'New York', 'San Francisco', 'London', 'Berlin'][i % 5],
    status: Math.random() > 0.3 ? 'active' : 'archived',
    tags: [
      ['React', 'TypeScript', 'JavaScript'],
      ['Node.js', 'Python', 'API'],
      ['Full Stack', 'React', 'Node.js'],
      ['AWS', 'Docker', 'Kubernetes'],
      ['Product', 'Strategy', 'Analytics']
    ][i % 5],
    order: i + 1,
    createdAt: new Date(Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000).toISOString(),
    updatedAt: new Date().toISOString()
  }));

  await db.jobs.bulkAdd(jobs);

  // Seed Candidates
  const stages = ['applied', 'screen', 'tech', 'offer', 'hired', 'rejected'];
  const candidates: Candidate[] = Array.from({ length: 1000 }, (_, i) => {
    const jobId = jobs[Math.floor(Math.random() * jobs.length)].id;
    const stage = stages[Math.floor(Math.random() * stages.length)];
    const appliedAt = new Date(Date.now() - Math.random() * 60 * 24 * 60 * 60 * 1000).toISOString();
    
    return {
      id: `candidate-${i + 1}`,
      name: `Candidate ${i + 1}`,
      email: `candidate${i + 1}@example.com`,
      phone: `+1-555-${String(Math.floor(Math.random() * 10000)).padStart(4, '0')}`,
      resume: `Resume for Candidate ${i + 1}`,
      status: 'active',
      stage,
      jobId,
      appliedAt,
      timeline: [
        {
          id: `timeline-${i + 1}-1`,
          stage: 'applied',
          timestamp: appliedAt,
          notes: 'Application submitted'
        }
      ]
    };
  });

  await db.candidates.bulkAdd(candidates);

  // Seed Assessments
  const assessments: Assessment[] = jobs.slice(0, 3).map((job, i) => ({
    id: `assessment-${job.id}`,
    jobId: job.id,
    title: `${job.title} Assessment`,
    description: `Technical assessment for ${job.title} position`,
    sections: [
      {
        id: `section-${i + 1}-1`,
        title: 'Technical Skills',
        questions: [
          {
            id: `q-${i + 1}-1`,
            type: 'single-choice',
            title: 'What is your experience with React?',
            description: 'Select your level of experience',
            required: true,
            options: ['Beginner', 'Intermediate', 'Advanced', 'Expert']
          },
          {
            id: `q-${i + 1}-2`,
            type: 'multi-choice',
            title: 'Which technologies have you worked with?',
            description: 'Select all that apply',
            required: true,
            options: ['JavaScript', 'TypeScript', 'React', 'Node.js', 'Python', 'Java']
          },
          {
            id: `q-${i + 1}-3`,
            type: 'short-text',
            title: 'Years of experience',
            description: 'How many years of professional experience do you have?',
            required: true,
            validation: { min: 0, max: 50 }
          },
          {
            id: `q-${i + 1}-4`,
            type: 'long-text',
            title: 'Describe your most challenging project',
            description: 'Tell us about a challenging project you worked on',
            required: true,
            validation: { maxLength: 1000 }
          },
          {
            id: `q-${i + 1}-5`,
            type: 'numeric',
            title: 'Expected salary range (in thousands)',
            description: 'What is your expected salary range?',
            required: false,
            validation: { min: 30, max: 300 }
          }
        ]
      },
      {
        id: `section-${i + 1}-2`,
        title: 'Behavioral Questions',
        questions: [
          {
            id: `q-${i + 1}-6`,
            type: 'single-choice',
            title: 'Do you prefer working in teams?',
            description: 'Select your preference',
            required: true,
            options: ['Yes', 'No', 'Sometimes']
          },
          {
            id: `q-${i + 1}-7`,
            type: 'long-text',
            title: 'Team collaboration experience',
            description: 'Describe your experience working in teams',
            required: true,
            conditional: {
              dependsOn: `q-${i + 1}-6`,
              condition: 'equals',
              value: 'Yes'
            },
            validation: { maxLength: 500 }
          },
          {
            id: `q-${i + 1}-8`,
            type: 'file-upload',
            title: 'Portfolio or work samples',
            description: 'Upload your portfolio or work samples',
            required: false
          }
        ]
      }
    ],
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  }));

  await db.assessments.bulkAdd(assessments);
};
