import React, { useState, useEffect } from 'react';
import { useParams, Link, useHistory } from 'react-router-dom';
import { Job, Candidate, ApiResponse } from '../types';
import JobModal from '../components/JobModal';

const JobDetail: React.FC = () => {
  const { jobId } = useParams<{ jobId: string }>();
  const history = useHistory();
  const [job, setJob] = useState<Job | null>(null);
  const [candidates, setCandidates] = useState<Candidate[]>([]);
  const [loading, setLoading] = useState(true);
  const [showEditModal, setShowEditModal] = useState(false);

  useEffect(() => {
    if (jobId) {
      loadJob();
      loadCandidatesForJob();
    }
  }, [jobId]);

  const loadJob = async () => {
    try {
      const response = await fetch(`/api/jobs/${jobId}`);
      if (!response.ok) throw new Error('Failed to load job');

      const data = await response.json();
      setJob(data.data);
    } catch (error) {
      console.error('Error loading job:', error);
    }
  };

  const loadCandidatesForJob = async () => {
    try {
      const response = await fetch(`/api/candidates?jobId=${jobId}&page=1&pageSize=100`);
      if (!response.ok) throw new Error('Failed to load candidates');

      const data: ApiResponse<Candidate[]> = await response.json();
      setCandidates(data.data);
    } catch (error) {
      console.error('Error loading candidates:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStageBadge = (stage: string) => {
    const badgeClasses = {
      applied: 'badge-info',
      screen: 'badge-warning',
      tech: 'badge-warning',
      offer: 'badge-success',
      hired: 'badge-success',
      rejected: 'badge-danger'
    };
    return <span className={`badge ${badgeClasses[stage as keyof typeof badgeClasses] || 'badge-info'}`}>{stage}</span>;
  };

  const getStatusBadge = (status: string) => {
    const badgeClass = status === 'active' ? 'badge-success' : 'badge-warning';
    return <span className={`badge ${badgeClass}`}>{status}</span>;
  };

  const handleEditJob = async (jobData: { title: string; description: string; location: string; tags: string[] }) => {
    if (!job) return;

    try {
      const response = await fetch(`/api/jobs/${job.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(jobData)
      });

      if (!response.ok) throw new Error('Failed to update job');

      setShowEditModal(false);
      loadJob(); // Reload job data
    } catch (error) {
      console.error('Error updating job:', error);
      alert('Failed to update job. Please try again.');
    }
  };

  const handleCreateAssessment = () => {
    // Navigate to assessments page with job pre-selected
    history.push(`/assessments?jobId=${job?.id}`);
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading job details...</p>
        </div>
      </div>
    );
  }

  if (!job) {
    return (
      <div className="container">
        <div className="text-center p-6">
          <h2>Job Not Found</h2>
          <p>The job you're looking for doesn't exist.</p>
          <Link to="/jobs" className="btn btn-primary">Back to Jobs</Link>
        </div>
      </div>
    );
  }

  const candidatesByStage = candidates.reduce((acc, candidate) => {
    if (!acc[candidate.stage]) {
      acc[candidate.stage] = [];
    }
    acc[candidate.stage].push(candidate);
    return acc;
  }, {} as Record<string, Candidate[]>);

  return (
    <div className="container">
      <div className="mb-6">
        <Link to="/jobs" className="btn btn-secondary mb-4">‚Üê Back to Jobs</Link>
      </div>

      <div className="card mb-6">
        <div className="card-header">
          <div className="flex justify-between items-start">
            <div>
              <h1 className="text-3xl font-bold mb-2">{job.title}</h1>
              <div className="flex items-center gap-4 mb-4">
                {getStatusBadge(job.status)}
                <span className="text-gray-600">üìç {job.location}</span>
                <span className="text-gray-600">üìÖ Posted {new Date(job.createdAt).toLocaleDateString()}</span>
              </div>
            </div>
            <div className="flex gap-2">
              <button className="btn btn-primary" onClick={() => setShowEditModal(true)}>Edit Job</button>
              <button className="btn btn-secondary" onClick={handleCreateAssessment}>Create Assessment</button>
            </div>
          </div>
        </div>

        <div className="mb-6">
          <h3 className="text-xl font-semibold mb-3">Job Description</h3>
          <p className="text-gray-700 leading-relaxed">{job.description}</p>
        </div>

        <div className="mb-6">
          <h3 className="text-xl font-semibold mb-3">Tags</h3>
          <div className="flex gap-2 flex-wrap">
            {job.tags.map(tag => (
              <span key={tag} className="badge badge-info">{tag}</span>
            ))}
          </div>
        </div>

        <div className="border-t pt-6">
          <div className="flex justify-between items-center">
            <div>
              <h3 className="text-xl font-semibold">Candidates</h3>
              <p className="text-gray-600">{candidates.length} total candidates</p>
            </div>
            <Link to={`/candidates?jobId=${job.id}`} className="btn btn-primary">
              View All Candidates
            </Link>
          </div>
        </div>
      </div>

      <div className="grid grid-2 gap-6">
        {Object.entries(candidatesByStage).map(([stage, stageCandidates]) => (
          <div key={stage} className="card">
            <div className="card-header">
              <h3 className="card-title flex items-center gap-2">
                {getStageBadge(stage)}
                <span className="text-lg">
                  {stage.charAt(0).toUpperCase() + stage.slice(1)} ({stageCandidates.length})
                </span>
              </h3>
            </div>

            <div className="space-y-3">
              {stageCandidates.slice(0, 5).map(candidate => (
                <div key={candidate.id} className="flex justify-between items-center p-3 border rounded">
                  <div>
                    <h4 className="font-semibold">{candidate.name}</h4>
                    <p className="text-sm text-gray-600">{candidate.email}</p>
                  </div>
                  <Link
                    to={`/candidates/${candidate.id}`}
                    className="btn btn-secondary btn-sm"
                  >
                    View Profile
                  </Link>
                </div>
              ))}

              {stageCandidates.length > 5 && (
                <div className="text-center p-3 text-gray-600">
                  And {stageCandidates.length - 5} more candidates...
                </div>
              )}

              {stageCandidates.length === 0 && (
                <div className="text-center p-6 text-gray-500">
                  No candidates in this stage
                </div>
              )}
            </div>
          </div>
        ))}
      </div>

      {Object.keys(candidatesByStage).length === 0 && (
        <div className="card text-center p-6">
          <h3 className="text-xl font-semibold mb-2">No Candidates Yet</h3>
          <p className="text-gray-600 mb-4">
            No candidates have applied for this job yet.
          </p>
          <Link to={`/candidates?jobId=${job.id}`} className="btn btn-primary">
            Manage Candidates
          </Link>
        </div>
      )}

      {/* Edit Job Modal */}
      {showEditModal && job && (
        <JobModal
          onSave={handleEditJob}
          onClose={() => setShowEditModal(false)}
          existingJob={{
            title: job.title,
            description: job.description,
            location: job.location,
            tags: job.tags,
          }}
          existingSlugs={[]} // Not needed for editing existing job
        />
      )}
    </div>
  );
};

export default JobDetail;
