import React, { useState } from 'react';

const LoginPage: React.FC<{ setRole: (role: string) => void }> = ({ setRole }) => {
  const [role, setLocalRole] = useState('');
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    if (!role || !username.trim()) {
      setError('Please select a role and enter your name.');
      return;
    }
    localStorage.setItem('role', role);
    localStorage.setItem('username', username);
    setRole(role);
  };

  return (
    <div className="container flex justify-center items-center" style={{ minHeight: '80vh' }}>
      <div className="card" style={{ maxWidth: 400 }}>
        <h2 className="mb-4 text-center">TalentFlow Login / Registration</h2>
        {error && <div className="error mb-2">{error}</div>}
        <div className="form-group">
          <label className="form-label">Role</label>
          <select className="form-input" value={role} onChange={e => setLocalRole(e.target.value)}>
            <option value="">Select Role</option>
            <option value="HR">HR</option>
            <option value="Candidate">Candidate</option>
          </select>
        </div>
        <div className="form-group">
          <label className="form-label">Name</label>
          <input
            type="text"
            className="form-input"
            placeholder="Enter your name"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
        </div>
        <button className="btn btn-primary w-full" onClick={handleLogin}>
          Login / Register
        </button>
      </div>
    </div>
  );
};

export default LoginPage;
