import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import {
  UserIcon,
  ClockIcon,
  CheckCircleIcon,
  EyeIcon,
  CalendarIcon
} from '@heroicons/react/24/outline';
import { AssessmentSubmission } from '../types';
import { apiCall } from '../utils/apiUtils';
import Card from './ui/Card';
import Button from './ui/Button';
import Modal from './ui/Modal';
import toast from 'react-hot-toast';

interface AssessmentSubmissionsProps {
  jobId: string;
  jobTitle: string;
}

const AssessmentSubmissions: React.FC<AssessmentSubmissionsProps> = ({ jobId, jobTitle }) => {
  const [submissions, setSubmissions] = useState<AssessmentSubmission[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedSubmission, setSelectedSubmission] = useState<AssessmentSubmission | null>(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    loadSubmissions();
  }, [jobId]);

  const loadSubmissions = async () => {
    try {
      setLoading(true);
      const response = await apiCall(`/api/assessments/${jobId}/submissions`);
      if (response.ok) {
        const data = await response.json();
        setSubmissions(data.data || []);
      }
    } catch (error) {
      console.error('Error loading submissions:', error);
      toast.error('Failed to load assessment submissions');
    } finally {
      setLoading(false);
    }
  };

  const handleViewSubmission = (submission: AssessmentSubmission) => {
    setSelectedSubmission(submission);
    setShowModal(true);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
          Assessment Submissions
        </h3>
        <span className="text-sm text-gray-500 dark:text-gray-400">
          {submissions.length} submission{submissions.length !== 1 ? 's' : ''}
        </span>
      </div>

      {submissions.length === 0 ? (
        <Card className="p-8 text-center">
          <UserIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <h4 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
            No Submissions Yet
          </h4>
          <p className="text-gray-600 dark:text-gray-400">
            No candidates have completed the assessment for this job.
          </p>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {submissions.map((submission, index) => (
            <motion.div
              key={submission.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              <Card className="p-4 hover:shadow-md transition-shadow">
                <div className="flex items-start justify-between mb-3">
                  <div className="flex items-center space-x-3">
                    <div className="w-10 h-10 bg-primary-100 dark:bg-primary-900 rounded-full flex items-center justify-center">
                      <UserIcon className="w-5 h-5 text-primary-600 dark:text-primary-400" />
                    </div>
                    <div>
                      <h4 className="font-medium text-gray-900 dark:text-white">
                        Candidate #{submission.candidateId.slice(-4)}
                      </h4>
                      <div className="flex items-center text-xs text-gray-500 dark:text-gray-400">
                        <CalendarIcon className="w-3 h-3 mr-1" />
                        {new Date(submission.submittedAt).toLocaleDateString()}
                      </div>
                    </div>
                  </div>
                  <CheckCircleIcon className="w-5 h-5 text-green-500" />
                </div>

                <div className="space-y-2 mb-4">
                  <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                    <ClockIcon className="w-4 h-4 mr-2" />
                    Submitted {new Date(submission.submittedAt).toLocaleTimeString()}
                  </div>
                  <div className="text-sm text-gray-600 dark:text-gray-300">
                    {Object.keys(submission.responses || {}).length} responses
                  </div>
                </div>

                <Button
                  variant="secondary"
                  size="sm"
                  onClick={() => handleViewSubmission(submission)}
                  icon={<EyeIcon className="w-4 h-4" />}
                  className="w-full"
                >
                  View Responses
                </Button>
              </Card>
            </motion.div>
          ))}
        </div>
      )}

      {/* Submission Detail Modal */}
      <Modal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        title="Assessment Submission"
      >
        {selectedSubmission && (
          <div className="space-y-6">
            <div className="border-b border-gray-200 dark:border-gray-700 pb-4">
              <h4 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                Candidate #{selectedSubmission.candidateId.slice(-4)}
              </h4>
              <div className="flex items-center space-x-4 text-sm text-gray-600 dark:text-gray-300">
                <div className="flex items-center">
                  <CalendarIcon className="w-4 h-4 mr-1" />
                  {new Date(selectedSubmission.submittedAt).toLocaleDateString()}
                </div>
                <div className="flex items-center">
                  <ClockIcon className="w-4 h-4 mr-1" />
                  {new Date(selectedSubmission.submittedAt).toLocaleTimeString()}
                </div>
              </div>
            </div>

            <div className="space-y-4">
              <h5 className="font-medium text-gray-900 dark:text-white">Responses:</h5>
              {selectedSubmission.responses && Object.keys(selectedSubmission.responses).length > 0 ? (
                <div className="space-y-3">
                  {Object.entries(selectedSubmission.responses).map(([questionId, response]) => (
                    <div key={questionId} className="bg-gray-50 dark:bg-gray-700 rounded-lg p-3">
                      <div className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                        Question {questionId}:
                      </div>
                      <div className="text-sm text-gray-900 dark:text-white">
                        {Array.isArray(response) ? response.join(', ') : String(response)}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500 dark:text-gray-400 text-sm">
                  No responses recorded for this submission.
                </p>
              )}
            </div>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default AssessmentSubmissions;
