      },
      table(tableName: string) {
        const downTable = core.table(tableName);
        const primKey = downTable.schema.primaryKey;
        const tableMW = {
          ...downTable,
          mutate(req: DBCoreMutateRequest): Promise<DBCoreMutateResponse> {
