import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Jobs from './pages/Jobs';
import Candidates from './pages/Candidates';
import Assessments from './pages/Assessments';
import Header from './components/common/Header';
import ErrorBoundary from './components/common/ErrorBoundary';
import LoginPage from './pages/LoginPage';
import JobsBoardForCandidates from './pages/JobsBoardForCandidates';
import MyApplications from './pages/MyApplications';
import { initializeDatabase } from './services/database';
import './styles/main.css';

const App: React.FC = () => {
  const [role, setRole] = useState(localStorage.getItem('role'));

  useEffect(() => {
    initializeDatabase();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('role');
    localStorage.removeItem('username');
    setRole(null);
  };

  if (!role) {
    return <LoginPage setRole={role => { localStorage.setItem('role', role); setRole(role); }} />;
  }

  return (
    <ErrorBoundary>
      <Router>
        <div className="min-h-screen bg-gray-50">
          <Header onLogout={handleLogout} />
          <main>
            <Switch>
              {role === 'HR' ? (
                <>
                  <Route path="/jobs" component={Jobs} />
                  <Route path="/candidates" component={Candidates} />
                  <Route path="/assessments" component={Assessments} />
                  <Route path="/" exact>
                    <div className="container">
                      <div className="text-center mb-6">
                        <h1 className="text-4xl font-bold mb-4">Welcome HR!</h1>
                        <p className="text-xl text-gray-600 mb-8">
                          Manage jobs, candidates, and assessments from your dashboard.
                        </p>
                      </div>
                      <div className="grid grid-3">
                        <div className="card text-center">
                          <div className="text-4xl mb-4">üíº</div>
                          <h3 className="text-lg font-semibold mb-2">Manage Jobs</h3>
                          <p className="text-gray-600 mb-4">
                            Create, edit, and organize job postings with drag-and-drop reordering.
                          </p>
                          <a href="/jobs" className="btn btn-primary">View Jobs</a>
                        </div>
                        <div className="card text-center">
                          <div className="text-4xl mb-4">üë•</div>
                          <h3 className="text-lg font-semibold mb-2">Track Candidates</h3>
                          <p className="text-gray-600 mb-4">
                            Manage candidate pipeline with kanban boards and detailed profiles.
                          </p>
                          <a href="/candidates" className="btn btn-primary">View Candidates</a>
                        </div>
                        <div className="card text-center">
                          <div className="text-4xl mb-4">üìù</div>
                          <h3 className="text-lg font-semibold mb-2">Create Assessments</h3>
                          <p className="text-gray-600 mb-4">
                            Build custom assessments with live preview and conditional questions.
                          </p>
                          <a href="/assessments" className="btn btn-primary">View Assessments</a>
                        </div>
                      </div>
                    </div>
                  </Route>
                  <Redirect to="/jobs" />
                </>
              ) : (
                <>
                  <Route path="/jobs" component={JobsBoardForCandidates} />
                  <Route path="/my-applications" component={MyApplications} />
                  <Route path="/" exact>
                    <div className="container">
                      <div className="text-center mb-6">
                        <h1 className="text-4xl font-bold mb-4">Welcome Candidate!</h1>
                        <p className="text-xl text-gray-600 mb-8">
                          Browse jobs, apply, and track your applications and assessments.
                        </p>
                      </div>
                      <div className="grid grid-2">
                        <div className="card text-center">
                          <div className="text-4xl mb-4">üíº</div>
                          <h3 className="text-lg font-semibold mb-2">Browse Jobs</h3>
                          <p className="text-gray-600 mb-4">
                            View available jobs and submit your application.
                          </p>
                          <a href="/jobs" className="btn btn-primary">View Jobs</a>
                        </div>
                        <div className="card text-center">
                          <div className="text-4xl mb-4">üìÑ</div>
                          <h3 className="text-lg font-semibold mb-2">My Applications</h3>
                          <p className="text-gray-600 mb-4">
                            Track your application status and complete assessments.
                          </p>
                          <a href="/my-applications" className="btn btn-primary">My Applications</a>
                        </div>
                      </div>
                    </div>
                  </Route>
                  <Redirect to="/jobs" />
                </>
              )}
            </Switch>
          </main>
        </div>
      </Router>
    </ErrorBoundary>
  );
};

export default App;
