import React, { useState, useEffect } from 'react';
import { assessmentsApi } from '../services/mockData';
import { Job } from '../types';

interface Assessment {
  id: string;
  jobId: string;
  title: string;
  description: string;
  sections: any[];
  createdAt: string;
  updatedAt: string;
}

interface AssessmentListProps {
  onCreateAssessment?: (jobId: string) => void;
}

const AssessmentList: React.FC<AssessmentListProps> = ({ onCreateAssessment }) => {
  const [assessments, setAssessments] = useState<Assessment[]>([]);
  const [jobs, setJobs] = useState<Job[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);

      // Load jobs
      const jobsResponse = await fetch('/api/jobs?page=1&pageSize=100');
      if (jobsResponse.ok) {
        const jobsData = await jobsResponse.json();
        setJobs(jobsData.data);
      }

      // Load existing assessments
      const assessmentsPromises = jobs.map(async (job) => {
        try {
          const assessment = await assessmentsApi.getAssessment(job.id);
          return assessment;
        } catch {
          return null;
        }
      });

      const assessmentsResults = await Promise.all(assessmentsPromises);
      const validAssessments = assessmentsResults.filter(a => a !== null) as Assessment[];
      setAssessments(validAssessments);

    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading assessments...</p>
        </div>
      </div>
    );
  }

  const jobsWithoutAssessments = jobs.filter(job =>
    !assessments.some(assessment => assessment.jobId === job.id)
  );

  return (
    <div className="container">
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Assessment Builder</h2>
          <div className="flex gap-2">
            <select
              className="form-input"
              onChange={(e) => {
                if (e.target.value && onCreateAssessment) {
                  onCreateAssessment(e.target.value);
                }
              }}
              defaultValue=""
            >
              <option value="">Create Assessment for Job</option>
              {jobsWithoutAssessments.map(job => (
                <option key={job.id} value={job.id}>
                  {job.title}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="grid grid-2">
          {assessments.map(assessment => {
            const job = jobs.find(j => j.id === assessment.jobId);
            return (
              <div key={assessment.id} className="card">
                <h3 className="text-lg font-semibold mb-2">{assessment.title}</h3>
                <p className="mb-2 text-sm text-gray-600">
                  Job: {job?.title || 'Unknown Job'}
                </p>
                <p className="mb-3 text-gray-600">{assessment.description}</p>

                <div className="mb-3">
                  <strong>Sections:</strong> {assessment.sections.length}
                </div>

                <div className="mb-3">
                  <strong>Questions:</strong> {assessment.sections.reduce((total, section) => total + section.questions.length, 0)}
                </div>

                <div className="flex gap-2">
                  <button
                    className="btn btn-primary"
                    onClick={() => onCreateAssessment && onCreateAssessment(assessment.jobId)}
                  >
                    Edit
                  </button>
                  <button className="btn btn-secondary">Preview</button>
                  <button className="btn btn-success">Publish</button>
                </div>
              </div>
            );
          })}
        </div>

        {assessments.length === 0 && (
          <div className="text-center p-6">
            <p>No assessments found. Select a job above to create your first assessment.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default AssessmentList;
