    updatedAt: new Date().toISOString()
  },
  {
    id: '3',
    title: 'Full Stack Developer - CloudTech',
    description: 'Looking for a full-stack developer to work on our cloud platform. Experience with React, Node.js, and AWS required.',
    location: 'Remote',
    status: 'archived',
    tags: ['React', 'Node.js', 'AWS'],
    order: 3,
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  }
];

export const mockCandidates = [
  {
    id: '1',
    name: 'John Doe',
    email: 'john.doe@email.com',
    phone: '+1-555-0123',
    resume: 'John_Doe_Resume.pdf',
    status: 'applied',
    stage: 'applied',
    jobId: '1',
    appliedAt: new Date().toISOString(),
    timeline: [{
      id: 'timeline-1',
      stage: 'applied',
      timestamp: new Date().toISOString(),
      notes: 'Application submitted'
    }]
  },
  {
    id: '2',
    name: 'Jane Smith',
    email: 'jane.smith@email.com',
    phone: '+1-555-0124',
    resume: 'Jane_Smith_Resume.pdf',
    status: 'screen',
