    timeline: [{
      id: 'timeline-2',
      stage: 'screen',
      timestamp: new Date().toISOString(),
      notes: 'Moved to screen stage'
    }]
  },
  {
    id: '3',
    name: 'Mike Johnson',
    email: 'mike.johnson@email.com',
    phone: '+1-555-0125',
    resume: 'Mike_Johnson_Resume.pdf',
    status: 'tech',
    stage: 'tech',
    jobId: '2',
    appliedAt: new Date().toISOString(),
    timeline: [{
      id: 'timeline-3',
      stage: 'tech',
      timestamp: new Date().toISOString(),
      notes: 'Technical interview scheduled'
    }]
  }
];

export const mockAssessments = [
  {
    id: 'assessment-1',
    jobId: '1',
    title: 'Frontend Developer Assessment',
    description: 'This assessment evaluates technical skills and problem-solving abilities for frontend development.',
    sections: [
      {
        id: 'section-1',
        title: 'Technical Skills',
        questions: [
          {
            id: 'q-1',
            type: 'single-choice',
            title: 'What is React?',
            description: 'Choose the best answer',
            required: true,
            options: ['A JavaScript library', 'A programming language', 'A database', 'A framework']
          },
          {
            id: 'q-2',
            type: 'short-text',
            title: 'Explain the concept of virtual DOM',
            description: 'Provide a brief explanation',
            required: true,
            validation: { maxLength: 200 }
          }
        ]
      }
    ],
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  }
];

// Simple API simulation
export const delay = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));

export const jobsApi = {
  getJobs: async () => {
    await delay(500);
    return { data: mockJobs, pagination: { page: 1, pageSize: 10, total: mockJobs.length, totalPages: 1 } };
  },
  createJob: async (job: any) => {
    await delay(300);
    const newJob = { ...job, id: Date.now().toString(), createdAt: new Date().toISOString(), updatedAt: new Date().toISOString() };
    mockJobs.push(newJob);
    return newJob;
  },
  updateJob: async (id: string, updates: any) => {
    await delay(300);
    const index = mockJobs.findIndex(job => job.id === id);
    if (index >= 0) {
      mockJobs[index] = { ...mockJobs[index], ...updates, updatedAt: new Date().toISOString() };
      return mockJobs[index];
    }
    throw new Error('Job not found');
  }
};

export const candidatesApi = {
  getCandidates: async () => {
    await delay(500);
    return { data: mockCandidates, pagination: { page: 1, pageSize: 20, total: mockCandidates.length, totalPages: 1 } };
  },
  createCandidate: async (candidate: any) => {
    await delay(300);
    const newCandidate = { ...candidate, id: Date.now().toString(), appliedAt: new Date().toISOString() };
    mockCandidates.push(newCandidate);
    return newCandidate;
  },
  updateCandidate: async (id: string, updates: any) => {
    await delay(300);
    const index = mockCandidates.findIndex(candidate => candidate.id === id);
    if (index >= 0) {
      mockCandidates[index] = { ...mockCandidates[index], ...updates };
      return mockCandidates[index];
    }
    throw new Error('Candidate not found');
  }
};

export const assessmentsApi = {
  getAssessment: async (jobId: string) => {
    await delay(300);
    const assessment = mockAssessments.find(a => a.jobId === jobId);
    if (!assessment) {
      throw new Error('Assessment not found');
    }
    return assessment;
  }
};
