import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable, DropResult } from 'react-beautiful-dnd';
import { Assessment, AssessmentSection, AssessmentQuestion } from '../types';

interface AssessmentBuilderProps {
  jobId: string;
  onSave: (assessment: Assessment) => void;
  onClose: () => void;
}

const questionTypes = [
  { value: 'single-choice', label: 'Single Choice' },
  { value: 'multi-choice', label: 'Multiple Choice' },
  { value: 'short-text', label: 'Short Text' },
  { value: 'long-text', label: 'Long Text' },
  { value: 'numeric', label: 'Numeric' },
  { value: 'file-upload', label: 'File Upload' }
];

const AssessmentBuilder: React.FC<AssessmentBuilderProps> = ({ jobId, onSave, onClose }) => {
  const [assessment, setAssessment] = useState<Assessment>({
    id: '',
    jobId,
    title: '',
    description: '',
    sections: [],
    createdAt: '',
    updatedAt: ''
  });

  const [activeSection, setActiveSection] = useState<number | null>(null);
  const [activeQuestion, setActiveQuestion] = useState<string | null>(null);

  useEffect(() => {
    loadAssessment();
  }, [jobId]);

  const loadAssessment = async () => {
    try {
      const response = await fetch(`/api/assessments/${jobId}`);
      if (response.ok) {
        const data = await response.json();
        if (data.data) {
          setAssessment(data.data);
        }
      }
    } catch (error) {
      console.error('Error loading assessment:', error);
    }
  };

  const handleSave = async () => {
    try {
      const response = await fetch(`/api/assessments/${jobId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(assessment)
      });

      if (!response.ok) throw new Error('Failed to save assessment');

      const data = await response.json();
      onSave(data.data);
    } catch (error) {
      console.error('Error saving assessment:', error);
      alert('Failed to save assessment. Please try again.');
    }
  };

  const addSection = () => {
    const newSection: AssessmentSection = {
      id: `section-${Date.now()}`,
      title: 'New Section',
      questions: []
    };

    setAssessment(prev => ({
      ...prev,
      sections: [...prev.sections, newSection]
    }));
  };

  const updateSection = (sectionIndex: number, updates: Partial<AssessmentSection>) => {
    setAssessment(prev => ({
      ...prev,
      sections: prev.sections.map((section, index) =>
        index === sectionIndex ? { ...section, ...updates } : section
      )
    }));
  };

  const deleteSection = (sectionIndex: number) => {
    setAssessment(prev => ({
      ...prev,
      sections: prev.sections.filter((_, index) => index !== sectionIndex)
    }));
  };

  const addQuestion = (sectionIndex: number) => {
    const newQuestion: AssessmentQuestion = {
      id: `question-${Date.now()}`,
      type: 'short-text',
      title: 'New Question',
      description: '',
      required: false,
      options: [],
      validation: {}
    };

    setAssessment(prev => ({
      ...prev,
      sections: prev.sections.map((section, index) =>
        index === sectionIndex
          ? { ...section, questions: [...section.questions, newQuestion] }
          : section
      )
    }));
  };

  const updateQuestion = (sectionIndex: number, questionIndex: number, updates: Partial<AssessmentQuestion>) => {
    setAssessment(prev => ({
      ...prev,
      sections: prev.sections.map((section, sIndex) =>
        sIndex === sectionIndex
          ? {
              ...section,
              questions: section.questions.map((question, qIndex) =>
                qIndex === questionIndex ? { ...question, ...updates } : question
              )
            }
          : section
      )
    }));
  };

  const deleteQuestion = (sectionIndex: number, questionIndex: number) => {
    setAssessment(prev => ({
      ...prev,
      sections: prev.sections.map((section, sIndex) =>
        sIndex === sectionIndex
          ? {
              ...section,
              questions: section.questions.filter((_, qIndex) => qIndex !== questionIndex)
            }
          : section
      )
    }));
  };

  const handleDragEnd = (result: DropResult) => {
    if (!result.destination) return;

    const { source, destination, type } = result;

    if (type === 'section') {
      // Reorder sections
      const sections = Array.from(assessment.sections);
      const [removed] = sections.splice(source.index, 1);
      sections.splice(destination.index, 0, removed);

      setAssessment(prev => ({ ...prev, sections }));
    } else if (type === 'question') {
      // Reorder questions within or between sections
      const sourceSectionIndex = parseInt(source.droppableId.split('-')[1]);
      const destSectionIndex = parseInt(destination.droppableId.split('-')[1]);

      const sourceSection = assessment.sections[sourceSectionIndex];
      const destSection = assessment.sections[destSectionIndex];

      const sourceQuestions = Array.from(sourceSection.questions);
      const [removed] = sourceQuestions.splice(source.index, 1);

      if (sourceSectionIndex === destSectionIndex) {
        // Same section
        sourceQuestions.splice(destination.index, 0, removed);
        updateSection(sourceSectionIndex, { questions: sourceQuestions });
      } else {
        // Different sections
        const destQuestions = Array.from(destSection.questions);
        destQuestions.splice(destination.index, 0, removed);

        setAssessment(prev => ({
          ...prev,
          sections: prev.sections.map((section, index) => {
            if (index === sourceSectionIndex) {
              return { ...section, questions: sourceQuestions };
            } else if (index === destSectionIndex) {
              return { ...section, questions: destQuestions };
            }
            return section;
          })
        }));
      }
    }
  };

  return (
    <div className="assessment-builder">
      <div className="builder-header">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold">Assessment Builder</h2>
          <div className="flex gap-2">
            <button className="btn btn-secondary" onClick={onClose}>
              Cancel
            </button>
            <button className="btn btn-primary" onClick={handleSave}>
              Save Assessment
            </button>
          </div>
        </div>

        <div className="grid grid-2 gap-6">
          <div className="builder-form">
            <div className="card mb-4">
              <h3 className="text-lg font-semibold mb-4">Assessment Details</h3>
              <div className="form-group">
                <label className="form-label">Title</label>
                <input
                  type="text"
                  className="form-input"
                  value={assessment.title}
                  onChange={(e) => setAssessment(prev => ({ ...prev, title: e.target.value }))}
                  placeholder="Assessment Title"
                />
              </div>
              <div className="form-group">
                <label className="form-label">Description</label>
                <textarea
                  className="form-input"
                  value={assessment.description}
                  onChange={(e) => setAssessment(prev => ({ ...prev, description: e.target.value }))}
                  placeholder="Assessment Description"
                  rows={3}
                />
              </div>
            </div>

            {/* @ts-ignore */}
            <DragDropContext onDragEnd={handleDragEnd}>
              <div className="sections-list">
                {assessment.sections.map((section, sectionIndex) => (
                  // @ts-ignore
                  <Draggable
                    key={section.id}
                    draggableId={`section-${sectionIndex}`}
                    index={sectionIndex}
                  >
                    {(provided: any) => (
                      <div
                        className="card mb-4"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                      >
                        <div className="section-header" {...provided.dragHandleProps}>
                          <input
                            type="text"
                            className="section-title-input"
                            value={section.title}
                            onChange={(e) => updateSection(sectionIndex, { title: e.target.value })}
                          />
                          <div className="flex gap-2">
                            <button
                              className="btn btn-secondary btn-sm"
                              onClick={() => addQuestion(sectionIndex)}
                            >
                              Add Question
                            </button>
                            <button
                              className="btn btn-danger btn-sm"
                              onClick={() => deleteSection(sectionIndex)}
                            >
                              Delete
                            </button>
                          </div>
                        </div>

                        {/* @ts-ignore */}
                        <Droppable droppableId={`section-${sectionIndex}`} type="question">
                          {(provided: any) => (
                            <div
                              className="questions-list"
                              ref={provided.innerRef}
                              {...provided.droppableProps}
                            >
                              {section.questions.map((question, questionIndex) => (
                                // @ts-ignore
                                <Draggable
                                  key={question.id}
                                  draggableId={`question-${question.id}`}
                                  index={questionIndex}
                                >
                                  {(provided: any) => (
                                    <div
                                      className="question-item"
                                      ref={provided.innerRef}
                                      {...provided.draggableProps}
                                      {...provided.dragHandleProps}
                                    >
                                      <div className="question-header">
                                        <select
                                          className="question-type-select"
                                          value={question.type}
                                          onChange={(e) => updateQuestion(sectionIndex, questionIndex, { type: e.target.value as any })}
                                        >
                                          {questionTypes.map(type => (
                                            <option key={type.value} value={type.value}>
                                              {type.label}
                                            </option>
                                          ))}
                                        </select>
                                        <button
                                          className="btn btn-danger btn-sm"
                                          onClick={() => deleteQuestion(sectionIndex, questionIndex)}
                                        >
                                          Ã—
                                        </button>
                                      </div>

                                      <input
                                        type="text"
                                        className="question-title-input"
                                        value={question.title}
                                        onChange={(e) => updateQuestion(sectionIndex, questionIndex, { title: e.target.value })}
                                        placeholder="Question title"
                                      />

                                      <textarea
                                        className="question-description-input"
                                        value={question.description}
                                        onChange={(e) => updateQuestion(sectionIndex, questionIndex, { description: e.target.value })}
                                        placeholder="Question description (optional)"
                                        rows={2}
                                      />

                                      {(question.type === 'single-choice' || question.type === 'multi-choice') && (
                                        <div className="options-editor">
                                          <label>Options:</label>
                                          {question.options?.map((option, optionIndex) => (
                                            <div key={optionIndex} className="option-item">
                                              <input
                                                type="text"
                                                value={option}
                                                onChange={(e) => {
                                                  const newOptions = [...(question.options || [])];
                                                  newOptions[optionIndex] = e.target.value;
                                                  updateQuestion(sectionIndex, questionIndex, { options: newOptions });
                                                }}
                                              />
                                              <button
                                                className="btn btn-danger btn-sm"
                                                onClick={() => {
                                                  const newOptions = (question.options || []).filter((_, i) => i !== optionIndex);
                                                  updateQuestion(sectionIndex, questionIndex, { options: newOptions });
                                                }}
                                              >
                                                Ã—
                                              </button>
                                            </div>
                                          ))}
                                          <button
                                            className="btn btn-secondary btn-sm"
                                            onClick={() => {
                                              const newOptions = [...(question.options || []), ''];
                                              updateQuestion(sectionIndex, questionIndex, { options: newOptions });
                                            }}
                                          >
                                            Add Option
                                          </button>
                                        </div>
                                      )}

                                      <div className="question-settings">
                                        <label>
                                          <input
                                            type="checkbox"
                                            checked={question.required}
                                            onChange={(e) => updateQuestion(sectionIndex, questionIndex, { required: e.target.checked })}
                                          />
                                          Required
                                        </label>
                                      </div>
                                    </div>
                                  )}
                                </Draggable>
                              ))}
                              {provided.placeholder}
                            </div>
                          )}
                        </Droppable>
                      </div>
                    )}
                  </Draggable>
                ))}
              </div>

              <button className="btn btn-primary" onClick={addSection}>
                Add Section
              </button>
            </DragDropContext>
          </div>

          <div className="preview-pane">
            <div className="card">
              <h3 className="text-lg font-semibold mb-4">Live Preview</h3>
              <div className="preview-content">
                {assessment.title && (
                  <h2 className="text-xl font-bold mb-2">{assessment.title}</h2>
                )}
                {assessment.description && (
                  <p className="text-gray-600 mb-4">{assessment.description}</p>
                )}

                {assessment.sections.map((section, sectionIndex) => (
                  <div key={section.id} className="preview-section mb-6">
                    <h3 className="text-lg font-semibold mb-3">{section.title}</h3>

                    {section.questions.map((question, questionIndex) => (
                      <div key={question.id} className="preview-question mb-4">
                        <div className="question-header">
                          <h4 className="font-medium">
                            {question.title}
                            {question.required && <span className="text-red-500 ml-1">*</span>}
                          </h4>
                          {question.description && (
                            <p className="text-sm text-gray-600 mt-1">{question.description}</p>
                          )}
                        </div>

                        <div className="question-input mt-2">
                          {question.type === 'single-choice' && (
                            <div className="space-y-2">
                              {question.options?.map((option, optionIndex) => (
                                <label key={optionIndex} className="flex items-center">
                                  <input type="radio" name={`q-${question.id}`} className="mr-2" />
                                  {option}
                                </label>
                              ))}
                            </div>
                          )}

                          {question.type === 'multi-choice' && (
                            <div className="space-y-2">
                              {question.options?.map((option, optionIndex) => (
                                <label key={optionIndex} className="flex items-center">
                                  <input type="checkbox" className="mr-2" />
                                  {option}
                                </label>
                              ))}
                            </div>
                          )}

                          {question.type === 'short-text' && (
                            <input type="text" className="form-input" placeholder="Your answer" />
                          )}

                          {question.type === 'long-text' && (
                            <textarea className="form-input" rows={4} placeholder="Your answer" />
                          )}

                          {question.type === 'numeric' && (
                            <input type="number" className="form-input" placeholder="Your answer" />
                          )}

                          {question.type === 'file-upload' && (
                            <input type="file" className="form-input" />
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                ))}

                {assessment.sections.length === 0 && (
                  <div className="text-center text-gray-500 py-8">
                    Add sections and questions to see the preview
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssessmentBuilder;
