import Dexie, { Table } from 'dexie';
import { Job, Candidate, Assessment, AssessmentSubmission } from '../types';

export class TalentFlowDB extends Dexie {
  jobs: Table<Job>;
  candidates: Table<Candidate>;
  assessments: Table<Assessment>;
  assessmentSubmissions: Table<AssessmentSubmission>;

  constructor() {
    super('TalentFlowDB');
    this.version(1).stores({
      jobs: 'id, title, status, order, createdAt',
      candidates: 'id, name, email, stage, jobId, appliedAt',
      assessments: 'id, jobId, title, createdAt',
      assessmentSubmissions: 'id, jobId, candidateId, submittedAt'
    });
    
    // Initialize tables
    this.jobs = this.table('jobs');
    this.candidates = this.table('candidates');
    this.assessments = this.table('assessments');
    this.assessmentSubmissions = this.table('assessmentSubmissions');
  }
}

export const db = new TalentFlowDB();

// Initialize database with seed data
export const initializeDB = async () => {
  try {
    // Check if data already exists
    const jobCount = await db.jobs.count();
    if (jobCount === 0) {
      // Import seed data dynamically to avoid circular imports
      const { seedData } = await import('../mocks/seedData');
      
      await db.jobs.bulkAdd(seedData.jobs);
      await db.candidates.bulkAdd(seedData.candidates);
      await db.assessments.bulkAdd(seedData.assessments);
      await db.assessmentSubmissions.bulkAdd(seedData.assessmentSubmissions);
      
      console.log('Database initialized with seed data');
    }
  } catch (error) {
    console.error('Error initializing database:', error);
  }
};
