import React, { useState, useEffect, useRef } from 'react';
import { Job } from '../types';
import { apiCall } from '../utils/apiUtils';

interface Assessment {
  id: string;
  jobId: string;
  title: string;
  description: string;
  sections: any[];
  createdAt: string;
  updatedAt: string;
}

interface AssessmentListProps {
  onCreateAssessment?: (jobId: string) => void;
}

const AssessmentList: React.FC<AssessmentListProps> = ({ onCreateAssessment }) => {
  const [assessments, setAssessments] = useState<Assessment[]>([]);
  const [jobs, setJobs] = useState<Job[]>([]);
  const [loading, setLoading] = useState(true);
  const isMountedRef = useRef(true);

  useEffect(() => {
    loadData();

    return () => {
      isMountedRef.current = false;
    };
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);

      // Load jobs first
      const jobsResponse = await apiCall('/api/jobs?page=1&pageSize=100');
      if (jobsResponse.ok) {
        const jobsData = await jobsResponse.json();
        const loadedJobs = jobsData.data || [];

        if (isMountedRef.current) {
          setJobs(loadedJobs);
        }

        // Load existing assessments for each job
        const assessmentsPromises = loadedJobs.map(async (job: Job) => {
          try {
            const response = await apiCall(`/api/assessments/${job.id}`);
            if (response.ok) {
              const data = await response.json();
              return data.data;
            }
            return null;
          } catch {
            return null;
          }
        });

        const assessmentsResults = await Promise.all(assessmentsPromises);
        const validAssessments = assessmentsResults.filter(a => a !== null) as Assessment[];

        if (isMountedRef.current) {
          setAssessments(validAssessments);
        }
      }
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      if (isMountedRef.current) {
        setLoading(false);
      }
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading assessments...</p>
        </div>
      </div>
    );
  }

  const jobsWithoutAssessments = jobs.filter(job =>
    !assessments.some(assessment => assessment.jobId === job.id)
  );

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700">
          <div className="p-6 border-b border-gray-200 dark:border-gray-700">
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
              <div>
                <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Assessment Builder</h2>
                <p className="text-gray-600 dark:text-gray-400 mt-1">Create and manage job assessments</p>
              </div>
              <div className="mt-4 sm:mt-0">
                <select
                  className="w-full sm:w-auto px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  onChange={(e) => {
                    console.log('Selected job ID:', e.target.value);
                    if (e.target.value && onCreateAssessment) {
                      console.log('Calling onCreateAssessment with:', e.target.value);
                      onCreateAssessment(e.target.value);
                      e.target.value = ''; // Reset dropdown
                    }
                  }}
                  defaultValue=""
                >
                  <option value="">Create Assessment for Job</option>
                  {jobsWithoutAssessments.map(job => (
                    <option key={job.id} value={job.id}>
                      {job.title}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          <div className="p-6">
            {assessments.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {assessments.map(assessment => {
                  const job = jobs.find(j => j.id === assessment.jobId);
                  return (
                    <div key={assessment.id} className="bg-gray-50 dark:bg-gray-700 rounded-lg p-6 border border-gray-200 dark:border-gray-600">
                      <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">{assessment.title}</h3>
                      <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                        Job: {job?.title || 'Unknown Job'}
                      </p>
                      <p className="text-gray-600 dark:text-gray-300 mb-4">{assessment.description}</p>

                      <div className="space-y-2 mb-4 text-sm">
                        <div className="flex justify-between">
                          <span className="text-gray-600 dark:text-gray-400">Sections:</span>
                          <span className="font-medium text-gray-900 dark:text-white">{assessment.sections.length}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600 dark:text-gray-400">Questions:</span>
                          <span className="font-medium text-gray-900 dark:text-white">
                            {assessment.sections.reduce((total, section) => total + section.questions.length, 0)}
                          </span>
                        </div>
                      </div>

                      <div className="flex flex-wrap gap-2">
                        <button
                          className="px-3 py-1.5 text-sm font-medium text-blue-600 dark:text-blue-400 hover:text-blue-700 dark:hover:text-blue-300 transition-colors"
                          onClick={() => onCreateAssessment && onCreateAssessment(assessment.jobId)}
                        >
                          Edit
                        </button>
                        <button className="px-3 py-1.5 text-sm font-medium text-gray-600 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300 transition-colors">
                          Preview
                        </button>
                        <button className="px-3 py-1.5 text-sm font-medium text-green-600 dark:text-green-400 hover:text-green-700 dark:hover:text-green-300 transition-colors">
                          Publish
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <div className="text-center py-12">
                <div className="text-6xl mb-4">📝</div>
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                  No assessments found
                </h3>
                <p className="text-gray-600 dark:text-gray-400 mb-6">
                  Select a job above to create your first assessment.
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssessmentList;
