import React, { useState } from 'react';

const AssessmentList: React.FC = () => {
    const [assessments, setAssessments] = useState<string[]>([]);
    const [newAssessment, setNewAssessment] = useState<string>('');

    const handleAddAssessment = () => {
        if (newAssessment.trim()) {
            setAssessments([...assessments, newAssessment]);
            setNewAssessment('');
        }
    };

    const handleDeleteAssessment = (index: number) => {
        const updatedAssessments = assessments.filter((_, i) => i !== index);
        setAssessments(updatedAssessments);
    };

    return (
        <div>
            <h2>Assessment List</h2>
            <input
                type="text"
                value={newAssessment}
                onChange={(e) => setNewAssessment(e.target.value)}
                placeholder="Add new assessment"
            />
            <button onClick={handleAddAssessment}>Add Assessment</button>
            <ul>
                {assessments.map((assessment, index) => (
                    <li key={index}>
                        {assessment}
                        <button onClick={() => handleDeleteAssessment(index)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AssessmentList;
