import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from 'react-query';
import { motion } from 'framer-motion';
import Jobs from './pages/Jobs';
import JobDetailProfessional from './pages/JobDetailProfessional';
import Candidates from './pages/Candidates';
import CandidateProfileProfessional from './pages/CandidateProfileProfessional';
import Assessments from './pages/Assessments';
import Header from './components/common/Header';
import ErrorBoundary from './components/common/ErrorBoundary';
import LoginPage from './pages/LoginPage';
import JobsBoardForCandidatesProfessional from './pages/JobsBoardForCandidatesProfessional';
import MyApplicationsProfessional from './pages/MyApplicationsProfessional';
import TakeAssessment from './pages/TakeAssessment';
import JobDetailForCandidate from './pages/JobDetailForCandidate';
import AnalyticsDashboard from './components/AnalyticsDashboard';
import OfflineIndicator from './components/OfflineIndicator';
import CandidateKanbanSimple from './components/CandidateKanbanSimple';
import ToastProvider from './components/ui/ToastProvider';
import { useAuth, useTheme } from './store';
import { initializeDatabase } from './services/database';
import { initializeUserDatabase } from './services/userDatabase';
import './styles/globals.css';

const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 2,
      staleTime: 5 * 60 * 1000, // 5 minutes
      cacheTime: 10 * 60 * 1000, // 10 minutes
    },
  },
});

const App: React.FC = () => {
  const { user, logout } = useAuth();
  const { theme } = useTheme();

  useEffect(() => {
    initializeDatabase();
    initializeUserDatabase();
    // Apply theme on mount
    document.documentElement.classList.toggle('dark', theme === 'dark');
  }, [theme]);

  if (!user.role) {
    return (
      <QueryClientProvider client={queryClient}>
        <ToastProvider />
        <LoginPage />
      </QueryClientProvider>
    );
  }

  return (
    <QueryClientProvider client={queryClient}>
      <ErrorBoundary>
        <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
          <ToastProvider />
          <div className="min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors duration-200">
            <Header onLogout={logout} />
            <OfflineIndicator />
            <motion.main
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.3 }}
              className="pb-8"
            >
              <Routes>
                {user.role === 'HR' ? (
                  <>
                    <Route path="/jobs/:jobId" element={<JobDetailProfessional />} />
                    <Route path="/jobs" element={<Jobs />} />
                    <Route path="/candidates/board" element={<CandidateKanbanSimple />} />
                    <Route path="/candidates/:id" element={<CandidateProfileProfessional />} />
                    <Route path="/candidates" element={<Candidates />} />
                    <Route path="/assessments" element={<Assessments />} />
                    <Route path="/" element={<HRDashboard />} />
                    <Route path="*" element={<Navigate to="/" replace />} />
                  </>
                ) : (
                  <>
                    <Route path="/jobs/:jobId" element={<JobDetailForCandidate />} />
                    <Route path="/jobs" element={<JobsBoardForCandidatesProfessional />} />
                    <Route path="/my-applications" element={<MyApplicationsProfessional />} />
                    <Route path="/assessment/:jobId" element={<TakeAssessment />} />
                    <Route path="/" element={<CandidateDashboard />} />
                    <Route path="*" element={<Navigate to="/" replace />} />
                  </>
                )}
              </Routes>
            </motion.main>
          </div>
        </Router>
      </ErrorBoundary>
    </QueryClientProvider>
  );
};

// Dashboard Components
const HRDashboard: React.FC = () => {
  const { user } = useAuth();

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-center mb-12"
      >
        <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
          Welcome back, {user.name}!
        </h1>
        <p className="text-xl text-gray-600 dark:text-gray-400 max-w-2xl mx-auto">
          Manage your hiring pipeline with powerful tools for jobs, candidates, and assessments.
        </p>
      </motion.div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-12">
        <DashboardCard
          title="Manage Jobs"
          description="Create, edit, and organize job postings with drag-and-drop reordering."
          icon="💼"
          href="/jobs"
          delay={0.1}
        />
        <DashboardCard
          title="Track Candidates"
          description="Manage candidate pipeline with kanban boards and detailed profiles."
          icon="👥"
          href="/candidates"
          delay={0.2}
        />
        <DashboardCard
          title="Create Assessments"
          description="Build custom assessments with live preview and conditional questions."
          icon="📝"
          href="/assessments"
          delay={0.3}
        />
      </div>

      {/* Analytics Dashboard */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4 }}
