import React, { useState, useEffect, useMemo } from 'react';
import { FixedSizeList as List } from 'react-window';
import { Candidate } from '../types';
import Badge from './ui/Badge';
import Input from './ui/Input';
import Card from './ui/Card';

interface VirtualizedCandidateListProps {
  candidates: Candidate[];
  onCandidateClick: (candidate: Candidate) => void;
  loading?: boolean;
}

const ITEM_HEIGHT = 120;
const CONTAINER_HEIGHT = 600;

const CandidateItem: React.FC<{
  index: number;
  style: React.CSSProperties;
  data: {
    candidates: Candidate[];
    onCandidateClick: (candidate: Candidate) => void;
  };
}> = ({ index, style, data }) => {
  const candidate = data.candidates[index];

  if (!candidate) return null;

  const getStageColor = (stage: string) => {
    const colors = {
      applied: 'bg-blue-100 text-blue-800',
      screen: 'bg-yellow-100 text-yellow-800',
      tech: 'bg-purple-100 text-purple-800',
      offer: 'bg-green-100 text-green-800',
      hired: 'bg-emerald-100 text-emerald-800',
      rejected: 'bg-red-100 text-red-800'
    };
    return colors[stage as keyof typeof colors] || 'bg-gray-100 text-gray-800';
  };

  const getInitials = (name: string) => {
    return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
  };

  return (
    <div style={style} className="px-4">
      <Card 
        className="h-24 p-4 cursor-pointer hover:shadow-md transition-shadow"
        onClick={() => data.onCandidateClick(candidate)}
      >
        <div className="flex items-center space-x-4 h-full">
          <div className="w-12 h-12 bg-primary-600 rounded-full flex items-center justify-center text-white font-semibold">
            {getInitials(candidate.name)}
          </div>

          <div className="flex-1 min-w-0">
            <div className="flex items-center justify-between">
              <h3 className="text-sm font-semibold text-gray-900 truncate">
                {candidate.name}
              </h3>
              <Badge className={getStageColor(candidate.stage)}>
                {candidate.stage}
              </Badge>
            </div>
            <p className="text-sm text-gray-600 truncate">{candidate.email}</p>
            <p className="text-xs text-gray-500">
              Applied {new Date(candidate.appliedAt).toLocaleDateString()}
            </p>
          </div>
        </div>
      </Card>
    </div>
  );
};

export const VirtualizedCandidateList: React.FC<VirtualizedCandidateListProps> = ({
  candidates,
  onCandidateClick,
  loading = false
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [stageFilter, setStageFilter] = useState('');

  const filteredCandidates = useMemo(() => {
    return candidates.filter(candidate => {
      const matchesSearch = !searchTerm || 
        candidate.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        candidate.email.toLowerCase().includes(searchTerm.toLowerCase());

      const matchesStage = !stageFilter || candidate.stage === stageFilter;

      return matchesSearch && matchesStage;
    });
  }, [candidates, searchTerm, stageFilter]);

  const stages = ['applied', 'screen', 'tech', 'offer', 'hired', 'rejected'];

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-4">
        <div className="flex-1">
          <Input
            type="text"
            placeholder="Search candidates by name or email..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="w-full sm:w-48">
          <select
            value={stageFilter}
            onChange={(e) => setStageFilter(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
          >
            <option value="">All Stages</option>
            {stages.map(stage => (
              <option key={stage} value={stage}>
                {stage.charAt(0).toUpperCase() + stage.slice(1)}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Results count */}
      <div className="text-sm text-gray-600">
        Showing {filteredCandidates.length} of {candidates.length} candidates
      </div>

      {/* Virtualized List */}
      <div className="border border-gray-200 rounded-lg overflow-hidden">
        {filteredCandidates.length > 0 ? (
          <List
            height={CONTAINER_HEIGHT}
            width="100%"
            itemCount={filteredCandidates.length}
            itemSize={ITEM_HEIGHT}
            itemData={{
              candidates: filteredCandidates,
              onCandidateClick
            }}
          >
            {CandidateItem}
          </List>
        ) : (
          <div className="flex items-center justify-center h-32 text-gray-500">
            No candidates found matching your criteria
          </div>
        )}
      </div>
    </div>
  );
};
