import React, { useState, useEffect } from 'react';
import { assessmentsApi } from '../services/mockData';

interface Assessment {
  id: string;
  jobId: string;
  title: string;
  description: string;
  sections: any[];
  createdAt: string;
  updatedAt: string;
}

const AssessmentList: React.FC = () => {
  const [assessments, setAssessments] = useState<Assessment[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadAssessments();
  }, []);

  const loadAssessments = async () => {
    try {
      setLoading(true);
      // For demo, we'll create a simple assessment
      const assessment = await assessmentsApi.getAssessment('1');
      setAssessments([assessment]);
    } catch (error) {
      console.error('Error loading assessments:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading assessments...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Assessment Builder</h2>
          <button className="btn btn-primary">Create Assessment</button>
        </div>

        <div className="grid grid-2">
          {assessments.map(assessment => (
            <div key={assessment.id} className="card">
              <h3 className="text-lg font-semibold mb-2">{assessment.title}</h3>
              <p className="mb-3 text-gray-600">{assessment.description}</p>

              <div className="mb-3">
                <strong>Sections:</strong> {assessment.sections.length}
              </div>

              <div className="mb-3">
                <strong>Questions:</strong> {assessment.sections.reduce((total, section) => total + section.questions.length, 0)}
              </div>

              <div className="flex gap-2">
                <button className="btn btn-primary">Edit</button>
                <button className="btn btn-secondary">Preview</button>
                <button className="btn btn-success">Publish</button>
              </div>
            </div>
          ))}
        </div>

        {assessments.length === 0 && (
          <div className="text-center p-6">
            <p>No assessments found. Create your first assessment to get started.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default AssessmentList;
