import React, { useState, useEffect, useRef } from 'react';
import { DragDropContext, Droppable, Draggable, DropResult } from 'react-beautiful-dnd';
import { Candidate, ApiResponse } from '../types';

const stages = ['applied', 'screen', 'tech', 'offer', 'hired', 'rejected'];

const CandidateKanban: React.FC = () => {
  const [candidates, setCandidates] = useState<Candidate[]>([]);
  const [loading, setLoading] = useState(true);
  const [draggedCandidate, setDraggedCandidate] = useState<Candidate | null>(null);
  const isMountedRef = useRef(true);

  useEffect(() => {
    loadCandidates();

    return () => {
      isMountedRef.current = false;
    };
  }, []);

  const loadCandidates = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/candidates?page=1&pageSize=1000');
      if (!response.ok) throw new Error('Failed to load candidates');

      const data: ApiResponse<Candidate[]> = await response.json();
      if (isMountedRef.current) {
        setCandidates(data.data);
      }
    } catch (error) {
      console.error('Error loading candidates:', error);
    } finally {
      if (isMountedRef.current) {
        setLoading(false);
      }
    }
  };

  const getCandidatesByStage = (stage: string) => {
    return candidates.filter(candidate => candidate.stage === stage);
  };

  const getStageBadge = (stage: string) => {
    const badgeClasses = {
      applied: 'badge-info',
      screen: 'badge-warning',
      tech: 'badge-warning',
      offer: 'badge-success',
      hired: 'badge-success',
      rejected: 'badge-danger'
    };
    return <span className={`badge ${badgeClasses[stage as keyof typeof badgeClasses] || 'badge-info'}`}>{stage}</span>;
  };

  const handleDragStart = (start: any) => {
    const candidate = candidates.find(c => c.id === start.draggableId);
    setDraggedCandidate(candidate || null);
  };

  const handleDragEnd = async (result: DropResult) => {
    setDraggedCandidate(null);

    if (!result.destination) return;

    const { source, destination, draggableId } = result;

    if (source.droppableId === destination.droppableId) return;

    const candidate = candidates.find(c => c.id === draggableId);
    if (!candidate) return;

    const newStage = destination.droppableId;
    const oldStage = candidate.stage;

    // Optimistic update
    const updatedCandidates = candidates.map(c =>
      c.id === draggableId ? { ...c, stage: newStage } : c
    );
    setCandidates(updatedCandidates);

    try {
      const response = await fetch(`/api/candidates/${draggableId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          stage: newStage,
          notes: `Moved from ${oldStage} to ${newStage}`
        })
      });

      if (!response.ok) throw new Error('Failed to update candidate stage');

      // Reload to get updated data from server
      loadCandidates();
    } catch (error) {
      console.error('Error updating candidate stage:', error);
      // Rollback on failure
      setCandidates(candidates);
      alert('Failed to update candidate stage. Please try again.');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-blue-600 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading candidates...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700 p-6">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Candidate Pipeline</h2>
        <p className="text-gray-600 dark:text-gray-400 mt-1">Drag candidates between stages to update their status</p>
      </div>

      <DragDropContext onDragStart={handleDragStart} onDragEnd={handleDragEnd}>
        <div className="flex gap-6 overflow-x-auto pb-4">
          {stages.map(stage => (
            <div key={stage} className="flex-shrink-0 w-80">
              <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4 h-full">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white capitalize">
                    {stage.charAt(0).toUpperCase() + stage.slice(1)}
                  </h3>
                  <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded-full text-sm font-medium">
                    {getCandidatesByStage(stage).length}
                  </span>
                </div>

                <Droppable droppableId={stage}>
                  {(provided: any, snapshot: any) => (
                    <div
                      className={`min-h-[200px] space-y-3 ${snapshot.isDraggingOver ? 'bg-blue-50 dark:bg-blue-900/20 rounded-lg' : ''}`}
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                    >
                      {getCandidatesByStage(stage).map((candidate, index) => (
                        <Draggable
                          key={candidate.id}
                          draggableId={candidate.id}
                          index={index}
                        >
                          {(provided: any, snapshot: any) => (
                            <div
                              className={`bg-white dark:bg-gray-800 rounded-lg p-4 border border-gray-200 dark:border-gray-600 cursor-grab active:cursor-grabbing transition-all ${
                                snapshot.isDragging ? 'rotate-2 scale-105 shadow-lg' : 'hover:shadow-md'
                              }`}
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                            >
                              <div className="flex items-start justify-between mb-3">
                                <h4 className="font-semibold text-gray-900 dark:text-white">{candidate.name}</h4>
                                {getStageBadge(candidate.stage)}
                              </div>
                              <div className="space-y-2 text-sm text-gray-600 dark:text-gray-400">
                                <div className="flex items-center">
                                  <span className="mr-2">ðŸ“§</span>
                                  <span className="truncate">{candidate.email}</span>
                                </div>
                                <div className="flex items-center">
                                  <span className="mr-2">ðŸ“ž</span>
                                  <span>{candidate.phone}</span>
                                </div>
                                <div className="flex items-center">
                                  <span className="mr-2">ðŸ“…</span>
                                  <span>Applied: {new Date(candidate.appliedAt).toLocaleDateString()}</span>
                                </div>
                              </div>
                              <div className="mt-4">
                                <a
                                  href={`/candidates/${candidate.id}`}
                                  className="inline-flex items-center px-3 py-1.5 text-sm font-medium text-blue-600 dark:text-blue-400 hover:text-blue-700 dark:hover:text-blue-300 transition-colors"
                                >
                                  View Profile â†’
                                </a>
                              </div>
                            </div>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
              </div>
            </div>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
};

export default CandidateKanban;
