import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Job, ApiResponse } from '../types';

const JobsBoardForCandidates: React.FC = () => {
  const [jobs, setJobs] = useState<Job[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [locationFilter, setLocationFilter] = useState('');
  const [tagFilter, setTagFilter] = useState('');
  const [appliedJobs, setAppliedJobs] = useState<Set<string>>(new Set());

  // Mock candidate ID - in real app this would come from authentication
  const candidateId = 'candidate-1';

  useEffect(() => {
    loadJobs();
    loadAppliedJobs();
  }, [searchTerm, locationFilter, tagFilter]);

  const loadJobs = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: '1',
        pageSize: '50',
        status: 'active'
      });

      if (searchTerm) params.append('search', searchTerm);

      const response = await fetch(`/api/jobs?${params}`);
      if (!response.ok) throw new Error('Failed to load jobs');

      const data: ApiResponse<Job[]> = await response.json();

      // Apply client-side filters
      let filteredJobs = data.data;

      if (locationFilter) {
        filteredJobs = filteredJobs.filter(job =>
          job.location.toLowerCase().includes(locationFilter.toLowerCase())
        );
      }

      if (tagFilter) {
        filteredJobs = filteredJobs.filter(job =>
          job.tags.some(tag => tag.toLowerCase().includes(tagFilter.toLowerCase()))
        );
      }

      setJobs(filteredJobs);
    } catch (error) {
      console.error('Error loading jobs:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadAppliedJobs = async () => {
    try {
      // Load candidate's applications to show which jobs they've already applied to
      const response = await fetch(`/api/candidates?email=candidate1@example.com`);
      if (response.ok) {
        const data = await response.json();
        const appliedJobIds = new Set(data.data.map((app: any) => app.jobId as string));
        setAppliedJobs(appliedJobIds);
      }
    } catch (error) {
      console.error('Error loading applied jobs:', error);
    }
  };

  const handleApply = async (job: Job) => {
    try {
      const applicationData = {
        name: 'John Doe', // Mock data - would come from user profile
        email: 'candidate1@example.com',
        phone: '+1-555-0123',
        resume: 'Resume content here',
        jobId: job.id,
        stage: 'applied'
      };

      const response = await fetch('/api/candidates', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(applicationData)
      });

      if (!response.ok) throw new Error('Failed to apply');

      setAppliedJobs(prev => new Set([...prev, job.id]));
      alert(`Successfully applied to ${job.title}!`);
    } catch (error) {
      console.error('Error applying to job:', error);
      alert('Failed to apply. Please try again.');
    }
  };

  const uniqueLocations = Array.from(new Set(jobs.map(job => job.location)));
  const uniqueTags = Array.from(new Set(jobs.flatMap(job => job.tags)));

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading available jobs...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="mb-6">
        <h2 className="text-2xl font-bold">Available Jobs</h2>
        <p className="text-gray-600 mt-2">Find your next opportunity and apply today</p>
      </div>

      {/* Search and Filters */}
      <div className="card mb-6">
        <div className="form-group flex gap-2 flex-wrap">
          <div className="flex-1 min-w-64">
            <input
              type="text"
              className="form-input"
              placeholder="Search jobs by title or keywords..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <select
            className="form-input"
            style={{ minWidth: '150px' }}
            value={locationFilter}
            onChange={(e) => setLocationFilter(e.target.value)}
          >
            <option value="">All Locations</option>
            {uniqueLocations.map(location => (
              <option key={location} value={location}>{location}</option>
            ))}
          </select>
          <select
            className="form-input"
            style={{ minWidth: '150px' }}
            value={tagFilter}
            onChange={(e) => setTagFilter(e.target.value)}
          >
            <option value="">All Skills</option>
            {uniqueTags.map(tag => (
              <option key={tag} value={tag}>{tag}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Jobs List */}
      <div className="space-y-4">
        {jobs.map(job => {
          const hasApplied = appliedJobs.has(job.id);

          return (
            <div key={job.id} className="card hover:shadow-lg transition-shadow">
              <div className="flex justify-between items-start">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <Link
                      to={`/jobs/${job.id}`}
                      className="text-xl font-semibold text-blue-600 hover:text-blue-800"
                    >
                      {job.title}
                    </Link>
                    <span className="badge badge-success">Active</span>
                  </div>

                  <p className="text-gray-600 mb-3 leading-relaxed">
                    {job.description.length > 200
                      ? `${job.description.substring(0, 200)}...`
                      : job.description
                    }
                  </p>

                  <div className="flex items-center gap-4 text-sm text-gray-600 mb-3">
                    <span>📍 {job.location}</span>
                    <span>🏢 {job.tags.slice(0, 3).join(', ')}</span>
                    <span>📅 Posted {new Date(job.createdAt).toLocaleDateString()}</span>
                  </div>

                  <div className="flex gap-2 flex-wrap">
                    {job.tags.map(tag => (
                      <span key={tag} className="badge badge-info">{tag}</span>
                    ))}
                  </div>
                </div>

                <div className="ml-6 flex flex-col gap-2">
                  <Link
                    to={`/jobs/${job.id}`}
                    className="btn btn-secondary"
                  >
                    View Details
                  </Link>

                  {hasApplied ? (
                    <span className="text-green-600 font-medium text-sm text-center py-2">
                      ✓ Applied
                    </span>
                  ) : (
                    <button
                      className="btn btn-primary"
                      onClick={() => handleApply(job)}
                    >
                      Apply Now
                    </button>
                  )}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {jobs.length === 0 && (
        <div className="card text-center p-8">
          <h3 className="text-xl font-semibold mb-2">No Jobs Found</h3>
          <p className="text-gray-600 mb-4">
            {searchTerm || locationFilter || tagFilter
              ? 'Try adjusting your search criteria to find more opportunities.'
              : 'No jobs are currently available. Check back later!'
            }
          </p>
          {(searchTerm || locationFilter || tagFilter) && (
            <button
              className="btn btn-secondary"
              onClick={() => {
                setSearchTerm('');
                setLocationFilter('');
                setTagFilter('');
              }}
            >
              Clear Filters
            </button>
          )}
        </div>
      )}

      {/* Application Tips */}
      <div className="card mt-8 bg-blue-50 border-blue-200">
        <h3 className="text-lg font-semibold text-blue-900 mb-3">💡 Application Tips</h3>
        <ul className="text-blue-800 space-y-2 text-sm">
          <li>• Review the job description carefully before applying</li>
          <li>• Ensure your resume is up to date and relevant to the position</li>
          <li>• Complete any assessments that may be required</li>
          <li>• Track your application status in "My Applications"</li>
        </ul>
      </div>
    </div>
  );
};

export default JobsBoardForCandidates;
