import React, { useState, useEffect } from 'react';
import { jobsApi } from '../services/mockData';

interface Job {
  id: string;
  title: string;
  description: string;
  location: string;
  status: string;
  tags: string[];
  order: number;
  createdAt: string;
  updatedAt: string;
}

const JobList: React.FC = () => {
  const [jobs, setJobs] = useState<Job[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('');

  useEffect(() => {
    loadJobs();
  }, []);

  const loadJobs = async () => {
    try {
      setLoading(true);
      const response = await jobsApi.getJobs();
      setJobs(response.data);
    } catch (error) {
      console.error('Error loading jobs:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredJobs = jobs.filter(job => 
    job.title.toLowerCase().includes(filter.toLowerCase()) ||
    job.description.toLowerCase().includes(filter.toLowerCase())
  );

  const getStatusBadge = (status: string) => {
    const badgeClass = status === 'active' ? 'badge-success' : 'badge-warning';
    return <span className={`badge ${badgeClass}`}>{status}</span>;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading jobs...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Job Listings</h2>
          <button className="btn btn-primary">Create Job</button>
        </div>

        <div className="form-group">
          <input
            type="text"
            className="form-input"
            placeholder="Search jobs..."
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
          />
        </div>

        <div className="grid grid-2">
          {filteredJobs.map(job => (
            <div key={job.id} className="card">
              <div className="flex justify-between items-center mb-2">
                <h3 className="text-lg font-semibold">{job.title}</h3>
                {getStatusBadge(job.status)}
              </div>

              <p className="mb-3 text-gray-600">{job.description}</p>

              <div className="mb-3">
                <strong>Location:</strong> {job.location}
              </div>

              <div className="mb-3">
                <strong>Tags:</strong>
                <div className="flex gap-2 mt-1">
                  {job.tags.map(tag => (
                    <span key={tag} className="badge badge-info">{tag}</span>
                  ))}
                </div>
              </div>

              <div className="flex gap-2">
                <button className="btn btn-secondary">Edit</button>
                <button className="btn btn-danger">Archive</button>
              </div>
            </div>
          ))}
        </div>

        {filteredJobs.length === 0 && (
          <div className="text-center p-6">
            <p>No jobs found matching your search.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default JobList;
