import React, { useState, useEffect } from 'react';
import { Assessment, AssessmentSubmission } from '../types';

interface AssessmentFormProps {
  assessment: Assessment;
  candidateId: string;
  onSubmit: (submission: AssessmentSubmission) => void;
  onClose: () => void;
}

const AssessmentForm: React.FC<AssessmentFormProps> = ({
  assessment,
  candidateId,
  onSubmit,
  onClose
}) => {
  const [responses, setResponses] = useState<Record<string, any>>({});
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [currentSection, setCurrentSection] = useState(0);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    // Initialize responses with default values
    const initialResponses: Record<string, any> = {};
    assessment.sections.forEach(section => {
      section.questions.forEach(question => {
        if (question.type === 'single-choice' || question.type === 'multi-choice') {
          initialResponses[question.id] = question.type === 'multi-choice' ? [] : '';
        } else if (question.type === 'numeric') {
          initialResponses[question.id] = '';
        } else {
          initialResponses[question.id] = '';
        }
      });
    });
    setResponses(initialResponses);
  }, [assessment]);

  const validateSection = (sectionIndex: number): boolean => {
    const section = assessment.sections[sectionIndex];
    const sectionErrors: Record<string, string> = {};
    let isValid = true;

    section.questions.forEach(question => {
      const value = responses[question.id];

      // Check required fields
      if (question.required) {
        if (question.type === 'multi-choice' && (!Array.isArray(value) || value.length === 0)) {
          sectionErrors[question.id] = 'This field is required';
          isValid = false;
        } else if (question.type !== 'multi-choice' && (!value || value.toString().trim() === '')) {
          sectionErrors[question.id] = 'This field is required';
          isValid = false;
        }
      }

      // Type-specific validation
      if (question.type === 'numeric' && value) {
        const numValue = parseFloat(value);
        if (isNaN(numValue)) {
          sectionErrors[question.id] = 'Please enter a valid number';
          isValid = false;
        } else {
          if (question.validation?.min !== undefined && numValue < question.validation.min) {
            sectionErrors[question.id] = `Value must be at least ${question.validation.min}`;
            isValid = false;
          }
          if (question.validation?.max !== undefined && numValue > question.validation.max) {
            sectionErrors[question.id] = `Value must be at most ${question.validation.max}`;
            isValid = false;
          }
        }
      }

      if (question.type === 'short-text' && question.validation?.maxLength && value.length > question.validation.maxLength) {
        sectionErrors[question.id] = `Maximum ${question.validation.maxLength} characters allowed`;
        isValid = false;
      }

      if (question.type === 'long-text' && question.validation?.maxLength && value.length > question.validation.maxLength) {
        sectionErrors[question.id] = `Maximum ${question.validation.maxLength} characters allowed`;
        isValid = false;
      }
    });

    setErrors(sectionErrors);
    return isValid;
  };

  const handleInputChange = (questionId: string, value: any) => {
    setResponses(prev => ({
      ...prev,
      [questionId]: value
    }));

    // Clear error for this field
    if (errors[questionId]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[questionId];
        return newErrors;
      });
    }
  };

  const handleNext = () => {
    if (validateSection(currentSection)) {
      if (currentSection < assessment.sections.length - 1) {
        setCurrentSection(currentSection + 1);
      }
    }
  };

  const handlePrevious = () => {
    if (currentSection > 0) {
      setCurrentSection(currentSection - 1);
    }
  };

  const handleSubmit = async () => {
    if (!validateSection(currentSection)) {
      return;
    }

    setIsSubmitting(true);

    try {
      const submission: AssessmentSubmission = {
        id: `submission-${Date.now()}`,
        jobId: assessment.jobId,
        candidateId,
        responses,
        submittedAt: new Date().toISOString()
      };

      await onSubmit(submission);
    } catch (error) {
      console.error('Error submitting assessment:', error);
      alert('Failed to submit assessment. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const checkCondition = (question: any): boolean => {
    if (!question.conditional) return true;

    const { dependsOn, condition, value } = question.conditional;
    const dependentValue = responses[dependsOn];

    switch (condition) {
      case 'equals':
        return dependentValue === value;
      case 'not-equals':
        return dependentValue !== value;
      case 'contains':
        if (Array.isArray(dependentValue)) {
          return dependentValue.includes(value);
        }
        return String(dependentValue).toLowerCase().includes(String(value).toLowerCase());
      case 'not-contains':
        if (Array.isArray(dependentValue)) {
          return !dependentValue.includes(value);
        }
        return !String(dependentValue).toLowerCase().includes(String(value).toLowerCase());
      default:
        return true;
    }
  };

  const renderQuestion = (question: any) => {
    // Check conditional logic
    if (!checkCondition(question)) {
      return null;
    }

    const value = responses[question.id];
    const error = errors[question.id];

    return (
      <div key={question.id} className="question-item mb-6">
        <div className="question-header mb-3">
          <h4 className="font-medium text-gray-900">
            {question.title}
            {question.required && <span className="text-red-500 ml-1">*</span>}
          </h4>
          {question.description && (
            <p className="text-sm text-gray-600 mt-1">{question.description}</p>
          )}
        </div>

        <div className="question-input">
          {question.type === 'single-choice' && (
            <div className="space-y-2">
              {question.options?.map((option: string, optionIndex: number) => (
                <label key={optionIndex} className="flex items-center">
                  <input
                    type="radio"
                    name={question.id}
                    value={option}
                    checked={value === option}
                    onChange={(e) => handleInputChange(question.id, e.target.value)}
                    className="mr-2"
                  />
                  {option}
                </label>
              ))}
            </div>
          )}

          {question.type === 'multi-choice' && (
            <div className="space-y-2">
              {question.options?.map((option: string, optionIndex: number) => (
                <label key={optionIndex} className="flex items-center">
                  <input
                    type="checkbox"
                    value={option}
                    checked={Array.isArray(value) && value.includes(option)}
                    onChange={(e) => {
                      const currentValues = Array.isArray(value) ? value : [];
                      if (currentValues.includes(option)) {
                        handleInputChange(question.id, currentValues.filter(v => v !== option));
                      } else {
                        handleInputChange(question.id, [...currentValues, option]);
                      }
                    }}
                    className="mr-2"
                  />
                  {option}
                </label>
              ))}
            </div>
          )}

          {question.type === 'short-text' && (
            <input
              type="text"
              className="form-input"
              value={value || ''}
              onChange={(e) => handleInputChange(question.id, e.target.value)}
              placeholder="Your answer"
              maxLength={question.validation?.maxLength}
            />
          )}

          {question.type === 'long-text' && (
            <textarea
              className="form-input"
              value={value || ''}
              onChange={(e) => handleInputChange(question.id, e.target.value)}
              placeholder="Your answer"
              rows={4}
              maxLength={question.validation?.maxLength}
            />
          )}

          {question.type === 'numeric' && (
            <input
              type="number"
              className="form-input"
              value={value || ''}
              onChange={(e) => handleInputChange(question.id, parseFloat(e.target.value) || '')}
              placeholder="Your answer"
              min={question.validation?.min}
              max={question.validation?.max}
            />
          )}

          {question.type === 'file-upload' && (
            <div className="file-upload-container">
              <input
                type="file"
                className="form-input"
                onChange={async (e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    // Convert file to base64 for storage
                    const reader = new FileReader();
                    reader.onload = (event) => {
                      const base64 = event.target?.result as string;
                      handleInputChange(question.id, {
                        name: file.name,
                        size: file.size,
                        type: file.type,
                        data: base64
                      });
                    };
                    reader.readAsDataURL(file);
                  } else {
                    handleInputChange(question.id, null);
                  }
                }}
                accept="image/*,.pdf,.doc,.docx"
              />
              {value && (
                <div className="file-preview mt-2 p-2 bg-gray-50 rounded border">
                  <div className="flex items-center gap-2">
                    <span className="text-sm font-medium">ðŸ“Ž {value.name}</span>
                    <span className="text-xs text-gray-500">
                      ({(value.size / 1024).toFixed(1)} KB)
                    </span>
                    <button
                      type="button"
                      className="text-red-500 hover:text-red-700 text-sm"
                      onClick={() => handleInputChange(question.id, null)}
                    >
                      âœ•
                    </button>
                  </div>
                  {value.type?.startsWith('image/') && (
                    <img
                      src={value.data}
                      alt={value.name}
                      className="mt-2 max-w-32 max-h-32 object-cover rounded border"
                    />
                  )}
                </div>
              )}
            </div>
          )}
        </div>

        {error && (
          <p className="text-red-500 text-sm mt-1">{error}</p>
        )}
      </div>
    );
  };

  const currentSectionData = assessment.sections[currentSection];
  const progress = ((currentSection + 1) / assessment.sections.length) * 100;

  return (
    <div className="assessment-form">
      <div className="form-header mb-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-bold">{assessment.title}</h2>
          <button className="btn btn-secondary" onClick={onClose}>
            Close
          </button>
        </div>

        {assessment.description && (
          <p className="text-gray-600 mb-4">{assessment.description}</p>
        )}

        <div className="progress-bar mb-4">
          <div className="progress-bg">
            <div
              className="progress-fill"
              style={{ width: `${progress}%` }}
            />
          </div>
          <div className="progress-text">
            Section {currentSection + 1} of {assessment.sections.length}
          </div>
        </div>
      </div>

      <div className="form-content">
        <div className="section-header mb-6">
          <h3 className="text-xl font-semibold">{currentSectionData.title}</h3>
        </div>

        <div className="questions-list">
          {currentSectionData.questions.map(renderQuestion)}
        </div>
      </div>

      <div className="form-footer mt-8">
        <div className="flex justify-between">
          <button
            className="btn btn-secondary"
            onClick={handlePrevious}
            disabled={currentSection === 0}
          >
            Previous
          </button>

          <div className="flex gap-2">
            {currentSection === assessment.sections.length - 1 ? (
              <button
                className="btn btn-primary"
                onClick={handleSubmit}
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Submitting...' : 'Submit Assessment'}
              </button>
            ) : (
              <button
                className="btn btn-primary"
                onClick={handleNext}
              >
                Next
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssessmentForm;
