import React, { useState, useEffect, useMemo } from 'react';
import { FixedSizeList as List } from 'react-window';
import { 
  MagnifyingGlassIcon, 
  FunnelIcon,
  UserIcon,
  EnvelopeIcon,
  PhoneIcon,
  CalendarIcon,
  EyeIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';
import { Candidate, Job, ApiResponse } from '../types';
import { apiCall } from '../utils/apiUtils';
import toast from 'react-hot-toast';


const ITEM_HEIGHT = 120;
const CONTAINER_HEIGHT = 600;

interface CandidateItemProps {
  index: number;
  style: React.CSSProperties;
  data: {
    candidates: Candidate[];
    jobs: Job[];
    onViewCandidate: (candidate: Candidate) => void;
  };
}

const CandidateItem: React.FC<CandidateItemProps> = ({ index, style, data }) => {
  const { candidates, jobs, onViewCandidate } = data;
  const candidate = candidates[index];
  const job = jobs.find(j => j.id === candidate.jobId);

  const getStageColor = (stage: string) => {
    const colors = {
      applied: 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200',
      screen: 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200',
      tech: 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200',
      offer: 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200',
      hired: 'bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-200',
      rejected: 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200'
    };
    return colors[stage as keyof typeof colors] || colors.applied;
  };

  return (
    <div style={style} className="px-4">
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-4 mx-2 my-1 hover:shadow-md transition-shadow">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4 flex-1">
            <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-blue-600 rounded-full flex items-center justify-center">
              <UserIcon className="w-6 h-6 text-white" />
            </div>

            <div className="flex-1 min-w-0">
              <div className="flex items-center space-x-3 mb-2">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white truncate">
                  {candidate.name}
                </h3>
                <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStageColor(candidate.stage)}`}>
                  {candidate.stage}
                </span>
              </div>

              <div className="flex items-center space-x-4 text-sm text-gray-600 dark:text-gray-400">
                <div className="flex items-center">
                  <EnvelopeIcon className="w-4 h-4 mr-1" />
                  <span className="truncate">{candidate.email}</span>
                </div>
                <div className="flex items-center">
                  <PhoneIcon className="w-4 h-4 mr-1" />
                  <span>{candidate.phone}</span>
                </div>
                <div className="flex items-center">
                  <CalendarIcon className="w-4 h-4 mr-1" />
                  <span>{new Date(candidate.appliedAt).toLocaleDateString()}</span>
                </div>
              </div>

              {job && (
                <div className="mt-1">
                  <span className="text-sm text-gray-500 dark:text-gray-400">
                    Applied to: <span className="font-medium text-gray-700 dark:text-gray-300">{job.title}</span>
                  </span>
                </div>
              )}
            </div>
          </div>

          <button
            onClick={() => onViewCandidate(candidate)}
            className="inline-flex items-center px-3 py-1.5 text-sm font-medium text-blue-600 dark:text-blue-400 hover:text-blue-700 dark:hover:text-blue-300 transition-colors"
          >
            <EyeIcon className="w-4 h-4 mr-1" />
            View
          </button>
        </div>
      </div>
    </div>
  );
};

const CandidateListVirtualized: React.FC = () => {
  const [candidates, setCandidates] = useState<Candidate[]>([]);
  const [jobs, setJobs] = useState<Job[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [stageFilter, setStageFilter] = useState('');
  const [selectedCandidate, setSelectedCandidate] = useState<Candidate | null>(null);
  const [candidateApplications, setCandidateApplications] = useState<Candidate[]>([]);
  const [modalLoading, setModalLoading] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);

      // Load all candidates (no pagination for virtualization)
      const candidatesResponse = await apiCall('/api/candidates?page=1&pageSize=10000');
      if (candidatesResponse.ok) {
        const candidatesData: ApiResponse<Candidate[]> = await candidatesResponse.json();
        setCandidates(candidatesData.data || []);
      }

      // Load jobs for reference
      const jobsResponse = await apiCall('/api/jobs?page=1&pageSize=1000');
      if (jobsResponse.ok) {
        const jobsData: ApiResponse<Job[]> = await jobsResponse.json();
        setJobs(jobsData.data || []);
      }
    } catch (error) {
      console.error('Error loading data:', error);
      toast.error('Failed to load candidates');
    } finally {
      setLoading(false);
    }
  };

  const filteredCandidates = useMemo(() => {
    let filtered = candidates;

    if (searchTerm.trim()) {
      const searchLower = searchTerm.toLowerCase();
      filtered = filtered.filter(candidate =>
        candidate.name.toLowerCase().includes(searchLower) ||
        candidate.email.toLowerCase().includes(searchLower)
      );
    }

    if (stageFilter) {
      filtered = filtered.filter(candidate => candidate.stage === stageFilter);
    }

    return filtered;
  }, [candidates, searchTerm, stageFilter]);

  const handleViewCandidate = async (candidate: Candidate) => {
    setSelectedCandidate(candidate);
    setModalLoading(true);

    try {
      const applicationsResponse = await apiCall(`/api/candidates?search=${encodeURIComponent(candidate.email)}&page=1&pageSize=100`);
      if (applicationsResponse.ok) {
        const applicationsData = await applicationsResponse.json();
        setCandidateApplications(applicationsData.data || []);
      }
    } catch (error) {
      console.error('Error loading candidate details:', error);
    } finally {
      setModalLoading(false);
    }
  };

  const handleModalClose = () => {
    setSelectedCandidate(null);
  };

  const handleRefresh = () => {
    loadData();
  };

  const stages = ['applied', 'screen', 'tech', 'offer', 'hired', 'rejected'];

  if (loading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Filters */}
      <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700 p-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="relative">
            <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search by name or email..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-400 focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
            />
          </div>

          <select
            value={stageFilter}
            onChange={(e) => setStageFilter(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
          >
            <option value="">All Stages</option>
            {stages.map(stage => (
              <option key={stage} value={stage}>
                {stage.charAt(0).toUpperCase() + stage.slice(1)}
              </option>
            ))}
          </select>

          <div className="flex items-center text-sm text-gray-500 dark:text-gray-400">
            <FunnelIcon className="w-4 h-4 mr-2" />
            {filteredCandidates.length} of {candidates.length} candidates
          </div>
        </div>
      </div>

      {/* Virtualized List */}
      <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700">
        <div className="p-6 border-b border-gray-200 dark:border-gray-700">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
            Candidates ({filteredCandidates.length})
          </h2>
        </div>

        {filteredCandidates.length > 0 ? (
          <List
            height={CONTAINER_HEIGHT}
            width="100%"
            itemCount={filteredCandidates.length}
            itemSize={ITEM_HEIGHT}
            itemData={{
              candidates: filteredCandidates,
              jobs,
              onViewCandidate: handleViewCandidate
            }}
          >
            {CandidateItem}
          </List>
        ) : (
          <div className="p-12 text-center">
            <UserIcon className="w-16 h-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
              No candidates found
            </h3>
            <p className="text-gray-600 dark:text-gray-400">
              {searchTerm || stageFilter
                ? 'Try adjusting your filters to see more results.'
                : 'No candidates have been added yet.'}
            </p>
          </div>
        )}
      </div>

      {/* Candidate Detail Modal */}
      {selectedCandidate && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-xl max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex items-center justify-between mb-6">
                <div className="flex items-center space-x-4">
                  <div className="w-16 h-16 bg-blue-600 rounded-full flex items-center justify-center text-white font-bold text-xl">
                    {selectedCandidate.name.charAt(0).toUpperCase()}
                  </div>
                  <div>
                    <h2 className="text-2xl font-bold text-gray-900 dark:text-white">{selectedCandidate.name}</h2>
                    <p className="text-gray-600 dark:text-gray-400">Candidate Profile</p>
                  </div>
                </div>
                <button
                  onClick={handleModalClose}
                  className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300"
                >
                  <XMarkIcon className="w-6 h-6" />
                </button>
              </div>

              {modalLoading ? (
                <div className="flex items-center justify-center py-8">
                  <div className="w-8 h-8 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
                </div>
              ) : (
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Contact Information</h3>
                    <div className="space-y-3">
                      <div className="flex items-center">
                        <EnvelopeIcon className="w-5 h-5 text-gray-400 mr-3" />
                        <span className="text-gray-700 dark:text-gray-300">{selectedCandidate.email}</span>
                      </div>
                      <div className="flex items-center">
                        <PhoneIcon className="w-5 h-5 text-gray-400 mr-3" />
                        <span className="text-gray-700 dark:text-gray-300">{selectedCandidate.phone}</span>
                      </div>
                      <div className="flex items-center">
                        <CalendarIcon className="w-5 h-5 text-gray-400 mr-3" />
                        <span className="text-gray-700 dark:text-gray-300">
                          Applied {new Date(selectedCandidate.appliedAt).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  </div>

                  <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Current Status</h3>
                    <div className="space-y-3">
                      <div>
                        <span className="text-sm text-gray-600 dark:text-gray-400">Current Stage:</span>
                        <div className={`inline-block ml-2 px-3 py-1 text-sm font-medium rounded-full ${
                          selectedCandidate.stage === 'applied' ? 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200' :
                          selectedCandidate.stage === 'screen' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200' :
                          selectedCandidate.stage === 'tech' ? 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200' :
                          selectedCandidate.stage === 'offer' ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200' :
                          selectedCandidate.stage === 'hired' ? 'bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-200' :
                          'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200'
                        }`}>
                          {selectedCandidate.stage}
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="lg:col-span-2">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">All Applications</h3>
                    <div className="space-y-3 max-h-60 overflow-y-auto">
                      {candidateApplications.map((app) => {
                        const job = jobs.find(j => j.id === app.jobId);
                        return (
                          <div key={app.id} className="flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-700 rounded-lg">
                            <div>
                              <h4 className="font-medium text-gray-900 dark:text-white">
                                {job?.title || 'Unknown Job'}
                              </h4>
                              <p className="text-sm text-gray-600 dark:text-gray-400">
                                Applied {new Date(app.appliedAt).toLocaleDateString()}
                              </p>
                            </div>
                            <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                              app.stage === 'applied' ? 'bg-blue-100 text-blue-800' :
                              app.stage === 'screen' ? 'bg-yellow-100 text-yellow-800' :
                              app.stage === 'tech' ? 'bg-purple-100 text-purple-800' :
                              app.stage === 'offer' ? 'bg-green-100 text-green-800' :
                              app.stage === 'hired' ? 'bg-emerald-100 text-emerald-800' :
                              'bg-red-100 text-red-800'
                            }`}>
                              {app.stage}
                            </span>
                          </div>
                        );
                      })}
                    </div>
                  </div>

                  <div className="lg:col-span-2">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Application Timeline</h3>
                    <div className="space-y-4 max-h-60 overflow-y-auto">
                      {selectedCandidate.timeline?.map((entry, index) => (
                        <div key={entry.id} className="flex items-start space-x-3">
                          <div className="w-8 h-8 bg-blue-100 dark:bg-blue-900 rounded-full flex items-center justify-center">
                            <span className="text-xs font-medium text-blue-600 dark:text-blue-400">{index + 1}</span>
                          </div>
                          <div className="flex-1">
                            <div className="flex items-center space-x-2">
                              <h4 className="font-medium text-gray-900 dark:text-white capitalize">{entry.stage}</h4>
                              <span className="text-sm text-gray-500 dark:text-gray-400">
                                {new Date(entry.timestamp).toLocaleDateString()}
                              </span>
                            </div>
                            <p className="text-sm text-gray-600 dark:text-gray-400">{entry.notes}</p>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CandidateListVirtualized;
