import React, { useState, useEffect } from 'react';
import { candidatesApi } from '../services/mockData';

interface Candidate {
  id: string;
  name: string;
  email: string;
  phone: string;
  resume: string;
  status: string;
  stage: string;
  jobId: string;
  appliedAt: string;
  timeline: any[];
}

const CandidateList: React.FC = () => {
  const [candidates, setCandidates] = useState<Candidate[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadCandidates();
  }, []);

  const loadCandidates = async () => {
    try {
      setLoading(true);
      const response = await candidatesApi.getCandidates();
      setCandidates(response.data);
    } catch (error) {
      console.error('Error loading candidates:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredCandidates = candidates.filter(candidate =>
    candidate.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    candidate.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const getStageBadge = (stage: string) => {
    const badgeClasses = {
      applied: 'badge-info',
      screen: 'badge-warning',
      tech: 'badge-warning',
      offer: 'badge-success',
      hired: 'badge-success',
      rejected: 'badge-danger'
    };
    return <span className={`badge ${badgeClasses[stage as keyof typeof badgeClasses] || 'badge-info'}`}>{stage}</span>;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading candidates...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Candidate Management</h2>
          <button className="btn btn-primary">Add Candidate</button>
        </div>

        <div className="form-group">
          <input
            type="text"
            className="form-input"
            placeholder="Search candidates..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="list">
          {filteredCandidates.map(candidate => (
            <div key={candidate.id} className="list-item">
              <div className="flex flex-col gap-2">
                <div className="flex items-center gap-4">
                  <h3 className="font-semibold">{candidate.name}</h3>
                  {getStageBadge(candidate.stage)}
                </div>
                <div className="text-sm text-gray-600">
                  <div>📧 {candidate.email}</div>
                  <div>📞 {candidate.phone}</div>
                  <div>📄 {candidate.resume}</div>
                </div>
              </div>

              <div className="flex gap-2">
                <button className="btn btn-secondary">View</button>
                <button className="btn btn-primary">Edit</button>
                <button className="btn btn-success">Move Stage</button>
              </div>
            </div>
          ))}
        </div>

        {filteredCandidates.length === 0 && (
          <div className="text-center p-6">
            <p>No candidates found matching your search.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CandidateList;
